function.textSource:
      function AfterPForm return boolean is
	l_chefsProjets VARCHAR2(4000);--PPM 63485 : modification de taille 4000
begin
	l_chefsProjets := pack_global.lire_globaldata(:P_GLOBAL).chefprojet;
	--srw.message(1, 'Chefs de projets : ' || l_chefsProjets);

	--srw.message(1,:P_where);
	--FAD QC 1880
	:P_WHERE := 'WHERE (INSTR(''' || l_chefsProjets || ''' ,TO_CHAR(lb.pcpi, ''FM00000'')) > 0 ';
	:P_WHERE := :P_WHERE || 'OR cons.ident in ( ';
	:P_WHERE := :P_WHERE || 'select distinct f.ident ';
	:P_WHERE := :P_WHERE || 'from situ_ress f, datdebex d ';
	:P_WHERE := :P_WHERE || 'where ';
	:P_WHERE := :P_WHERE || '((TRUNC(f.datsitu,''YEAR'') <= TRUNC(d.DATDEBEX,''YEAR'') OR f.datsitu IS NULL) ';
	:P_WHERE := :P_WHERE || 'AND (TRUNC(f.datdep,''YEAR'') >= TRUNC(d.DATDEBEX,''YEAR'') OR f.datdep IS NULL)) ';
	:P_WHERE := :P_WHERE || 'and ((INSTR(''' || l_chefsProjets || ''', TO_CHAR(f.cpident, ''FM00000''))>0) ';
	:P_WHERE := :P_WHERE || 'or(INSTR(''' || l_chefsProjets || ''', TO_CHAR(f.ident, ''FM00000''))>0))))';

	return (TRUE);
end;
