      function AfterPForm return boolean is
	l_menu VARCHAR2(25);
	p_dpg VARCHAR2(10) := '';--PPM 61346
begin
  	  	
	SELECT TO_NUMBER(TO_CHAR(datdebex.moismens,'YYYY')),
  			TO_CHAR(datdebex.moismens) INTO :P_ANNEE , :P_MOISMENS
  FROM datdebex;
	
	-- Initialisation des paramètres	  
  :P_habili_DP := '';
  :P_habili_P  := '';
  :P_habili_A  :='';
 	:P_PERIME:='';
	:P_PERIMCLI:='';
	:P_HAB := 'VIDE';
	:P_WHERE := '';   	
  
   -- Teste si on a été lancé par le menu habilitation par référentiels
	IF :P_PARAM8='REF' THEN
  
	   -- Informations pour l'habilitation par référentiels
		:P_habili_DP := pack_global.lire_doss_proj(:P_GLOBAL);
	  :P_habili_P  := pack_global.lire_projet(:P_GLOBAL);
	  :P_habili_A  := pack_global.lire_appli(:P_GLOBAL);

	  IF (:P_habili_DP is null or :P_habili_DP='') THEN
			  :P_habili_DP := 'VIDE';
	  END IF;	
	  IF (:P_habili_P is null or :P_habili_P='') THEN
			  :P_habili_P := 'VIDE';
	  END IF;	
	  IF (:P_habili_A is null or :P_habili_A='') THEN
			  :P_habili_A := 'VIDE';
	  END IF;	
  
--		--srw.message(1,:P_habili_DP);
--		--srw.message(1,:P_habili_P);
--		--srw.message(1,:P_habili_A);
  	
	  -- Si on a une valeur égale à TOUS - on ne fait aucune Restriction
	  IF (:P_habili_DP='TOUS' or :P_habili_P='TOUS' or :P_habili_A='TOUS' )THEN
  		:P_HAB := '';
  	ELSE	

		  IF (:P_habili_DP<>'VIDE')THEN
			  :P_HAB := ' lb.dpcode in ('||:P_habili_DP||') ';
		  END IF;		
		  -- Projet demandé
		  -- On fait aussi le contrôle que sur le projet principal (icpir)
		  IF (:P_habili_P<>'VIDE') THEN
		  		IF (:P_HAB<>'VIDE') THEN  	
		  			:P_HAB := :P_HAB 	||' OR ' ;
		  		ELSE
		  			:P_HAB := '' ;
		  		END IF;	
					:P_HAB := :P_HAB ||' (INSTR('''||:P_habili_P||''', lb.icpi) > 0) ';		  
		  END IF;
										
			-- Application demandée
		  -- On fait aussi le contrôle sur l'application principale
		  IF (:P_habili_A<>'VIDE') THEN
			  	IF (:P_HAB<>'VIDE') THEN  	
			  		:P_HAB := :P_HAB 	||' OR ' ;
		  		ELSE
		  			:P_HAB := '' ;
			  	END IF;	
			 	  :P_HAB := :P_HAB ||' (INSTR('''||:P_habili_A||''', lb.airt) > 0) ' ; 
		  END IF;
		  
		  -- On rajoute le 'AND devant P_HAB
	  	IF (:P_HAB<>'VIDE') THEN  	
		  		:P_HAB := ' AND ( ' || :P_HAB || ' ) ' ;
		  		--srw.message(1,:P_HAB);
	  	ELSE 
	  		:P_HAB := '';
	  	END IF;	
		END IF;  		  
	-- Le traitment n'a pas été lancé par le menu referentiel (il a pu été lancé par exemple par le menu administration )	
  ELSE 
	   -- Informations pour le lancement par les autres menus 	
		:P_PERIME:=pack_global.lire_perime(:P_GLOBAL);
		
		if INSTR(:P_PERIME, '00000000000') > 0
			then
				:P_PERIME:= '06320000000';	
			end if;
		
		
		  /*CMA Fiche 1176 09/05/2011 le perimo est différent si l'utilisateur est dans le menu Client*/
		l_menu := pack_global.lire_menutil(:P_global);
		
		if('MO'=l_menu)then
			:P_PERIMCLI := pack_global.lire_perimcli(:P_global);
		else
			:P_PERIMCLI := pack_global.lire_perimo(:P_global);
		end if;
		
		:P_HAB := '';

  --QC1758 debut	:P_WHERE :=' AND ((LB.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''||:P_PERIME||''', codbddpg) > 0 ))'; 
				--KRA PPM 61346
		p_dpg := RTRIM(LTRIM(:P_PARAM9));		
		
		IF LPAD(p_dpg, 7, '*') != '*******' OR p_dpg IS NULL THEN
		p_dpg := REPLACE(p_dpg,'*','%');
			--DPG de ligne			
			:P_WHERE := :P_WHERE || ' AND (TO_CHAR(LB.codsg, ''FM0000000'') like ''' || p_dpg || '''';
		ELSIF p_dpg = '*******' THEN
			--DPG de ligne
			:P_WHERE := :P_WHERE || ' AND (( LB.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''||:P_PERIME||''', codbddpg) > 0 ))';
		END IF;	
		--Fin KRA PPM 61346
  --QC 1758 fin
		IF :P_PARAM7='FOUR_CLI' THEN
			IF ( LENGTH(:P_PERIMCLI) != 0 AND INSTR(:P_PERIMCLI,'000000000')=0 AND INSTR(:P_PERIMCLI,'099988888')=0 ) THEN
				:P_WHERE := :P_WHERE || ' OR ((LB.clicode IN (SELECT clicode FROM vue_clicode_perimo where INSTR('''||:P_PERIMCLI||''', bdclicode) > 0 )) OR (LB.clicode_oper IN (SELECT clicode FROM vue_clicode_perimo where INSTR('''||:P_PERIMCLI||''', bdclicode) > 0 )) )';
			END IF;
		END IF;
		:P_WHERE := :P_WHERE || ')';
		
	  ----srw.message(1,:p_perime|| 'et' || :p_perimcli || ' et '||:P_WHERE);
	END IF;		
    
  return (TRUE);
end;

      function P_PERIMOValidTrigger return boolean is
begin
  
  return (TRUE);
end;
