  l_habil_cafi := l_global.ca_fi; 	
  l_habil_ca_payeur := l_global.ca_payeur;
  l_habil_cada  := l_global.ca_da;	
  
   -- Dossier Projet :
	if(trim(:P_PARAM7) = 'Pas de limitation') then
