	elsif(trim(upper(:P_PARAM8)) = 'TOUS')	then
		if(l_habil_p IS NOT NULL AND l_habil_p <> 'TOUS' ) then
  		chaine_projet := l_habil_p; -- seulement les projets actifs
		else
			chaine_projet := 'VIDE';	
		end if;	
	else
		chaine_projet := :P_PARAM8;
	end if;	

	--srw.message(1,'chaine_projet : '||chaine_projet);

