      function AfterPForm return boolean is
anneecour number;
mois varchar(10);
begin

 
  select to_number(to_char(datdebex,'YYYY')),TO_CHAR(moismens, 'MM/YYYY') into anneecour,mois
  from datdebex;

  :P_MOIS := mois;            -- mois de la dernière mensuelle
  :P_ANNEE := anneecour;
  :P_ANNEE1 := anneecour-1;
  :P_ANNEE2 := anneecour-2;
  :P_ANNEE3 := anneecour-3;
  :P_ANNEE4 := anneecour-4;
  :P_ANNEE5 := anneecour-5;
  :P_ANNEE6 := anneecour-6;
  :P_ANNEE7 := anneecour-7;
  :P_ANNEE8 := anneecour-8;
  :P_ANNEE9 := anneecour-9;
  return (TRUE);
end;

