function.textSource:
      function AfterPForm return boolean is
begin
  if :P_PARAM6 = '1' then 
	:p_where := 'and bip.typproj = ''' || '1' || ''' ';
	:p_libtyp := '---                Projets de type 1                 ---';
  else 
	:p_where := 'and bip.typproj in (''' || '1' || ''',
					 ''' || '2' || ''',
					 ''' || '3' || ''',
					 ''' || '4' || ''',
					 ''' || '8' || ''')';
	:p_libtyp := '---          Projets de type 1,2,3,4 ou 8            ---';

  end if;

  if :P_PARAM7 = 'SOGENAL' then
	:p_where := :p_where || ' and (bip.codpspe=''' || 'S' || ''')';
	:p_libspe := '---                 SOGENAL, code S                  ---';

  else	if :P_PARAM7 = 'MUE' then
	:p_where := :p_where || ' and (bip.codpspe=''' || 'M' || '''
				    or bip.codpspe=''' || 'E' || ''')';
	:p_libspe := '---                 MUE, code M ou E                 ---';
	end if;
  end if;

  --srw.message(1,:p_where);  

  return (TRUE);
end;

      function CF_AN_MOINS1Formula return Number is
	l_res NUMBER(4);
begin
  	l_res := TO_NUMBER(:dc_ancouran) - 1;
	RETURN(l_res);
end;

