      function R_MUEAMTL2FormatTrigger return boolean is
begin
  IF	(:dc_mt_an_cour = 0)
	 and (:dc_mt_an_moins1 =0) 
	 and (:dc_mt_an_moins2 =0)
	 and (:dc_mt_an_moins3 =0)
	 and (:dc_mt_an_moins4 =0) 
  THEN
	RETURN(FALSE);
  ELSE
  	return (TRUE);
  END IF;
end;

