      function AfterPForm return boolean is
begin
  :p_where := '';
  if (:p_param6 is not null) then
  	:p_where := :p_where || ' and icpi = ''' || :p_param6 || '''';
  end if;
  
   if (:p_param7 is not null) then
  	:p_where := :p_where || ' and to_number(to_char(cdeb,''YYYY'')) =' || :p_param7 ;
  end if;
  	
  return (TRUE);
end;

      function F_JHFormatTrigger return boolean is

l_menu VARCHAR2(10);

begin
  
  l_menu := pack_global.lire_globaldata(:p_global).menutil;
  if (l_menu = 'REQ') then
  	return(false);
  else
  	return (TRUE);
  end if;
end;

