function.textSource:
      function CF_1Formula return VARCHAR2 is

resultat VARCHAR2(30);

begin
	SELECT libdsg
	INTO resultat
	FROM struct_info 
	WHERE struct_info.codsg=:codsg;

	return(resultat);
end;

      function AfterPForm return boolean is

	l_param 		VARCHAR2(7);

begin
  
	:p_where :='';

   IF NVL(LENGTH(LPAD(:P_PARAM6, 7, '0')), 0) != 0 then 
   		-- ajout de '0' a gauche sur 7 caracteres + suppression des '*'
      l_param := rtrim(rtrim(LPAD(:P_PARAM6, 7, '0'),'*')) ;
			:p_where := :p_where || ' and  TO_CHAR(bjh_ressource.codsg, ''FM0000000'') like ''' || l_param ||'%'' ';
   END IF;
     		
	--srw.message(1,:P_where);  
	
  return (true);
  end;
