dataSource.select:
      select clilib 
from client_mo
where clicode = :P_PARAM4;

function.textSource:
      function AfterPForm return boolean is
   l_annee0   VARCHAR2(10);
   l_annee1   VARCHAR2(10);
   l_dir      VARCHAR2(10);
   p_perimcli VARCHAR2(1000);
	 l_perimcli   VARCHAR2(1000);	  
	 p_where_perim VARCHAR2(1000); 
	 l_menu VARCHAR2(25); 
	  
begin
   -- ================================================
   -- Determiner l'annee de traitement N et N+1
   -- ================================================
   l_annee0 := to_char(add_months(sysdate,-1),'YYYY');
   l_annee1 := to_char(to_number(l_annee0,'FM9999') + 1,'FM9999');
   :P_ANNEE := l_annee0;
   :P_ANNEE1 := l_annee1;
   :p_where :=             ' and budg.annee(+) = ' || l_annee0 ;
   :p_where := :p_where || ' and budg1.annee(+) = ' || l_annee1 ;
   :p_where := :p_where || ' and conso.annee(+) = '  || l_annee0 ;
	 
 	  /*CMA Fiche 1176 09/05/2011 le perimo est différent si l'utilisateur est dans le menu Client*/
		l_menu := pack_global.lire_menutil(:P_global);
		
		if('MO'=l_menu)then
			l_perimcli := pack_global.lire_perimcli(:P_global);
		else
			l_perimcli := pack_global.lire_perimo(:P_global);
		end if;
	 
   -- 02/03/2001 : gestion des habilitations => direction ou département actif
    BEGIN
	   	select 'dir' into l_dir
				from client_mo
				where clicode=:p_param4;
    EXCEPTION
			WHEN NO_DATA_FOUND THEN   --départment
				l_dir :='dpt'; 
    END;

		if length(l_perimcli) != 0 then	  	
	  p_where_perim := ' AND (';
		while (length(l_perimcli) != 0) loop 
	   if (instr(l_perimcli,',') != 0) then
	      p_perimcli := substr(l_perimcli,0,instr(l_perimcli,',')-1);
	      l_perimcli := substr(l_perimcli,instr(l_perimcli,',')+1);
	      p_where_perim := p_where_perim || 'DECODE('''||:P_PARAM6||''', ''OPER'', lb.clicode_oper, lb.clicode)  in (select clicode from vue_clicode_perimo where INSTR('''||p_perimcli||''',BDCLICODE)>0) or ';
	   else
	      p_perimcli := l_perimcli;
	      l_perimcli := '';
	      p_where_perim := p_where_perim || 'DECODE('''||:P_PARAM6||''', ''OPER'', lb.clicode_oper, lb.clicode)  in (select clicode from vue_clicode_perimo where INSTR('''||p_perimcli||''',BDCLICODE)>0) ';
	   end if;  
		END LOOP;
	   p_where_perim := p_where_perim || ')';
	  end if;
		--srw.message(1,p_where_perim);  
		:p_where := :p_where || p_where_perim;
  -- IF NVL(LENGTH(:P_PARAM4), 0)!= 0  THEN
	--:p_where := :p_where || ' and rtrim(ltrim(cm.clicode)) = ''' || rtrim(ltrim(:p_param4)) ||  '''' ;
   --END IF;
    --srw.message(1,:P_where);  
  return (TRUE);
end;

      function CF_AnneeFormula return VARCHAR2 is
   l_mois_annee  VARCHAR2(20);
begin
   -- ====================================================================
   -- Determiner l'anne de traitement N qui est l'annee du mois precedent
   -- ====================================================================
   l_mois_annee := to_char(add_months(sysdate,-1),'MONTH YYYY');
   return l_mois_annee;
end;

      function CF_an1Formula return VARCHAR2 is
   l_annee0  VARCHAR2(10);
   l_annee1  VARCHAR2(10);
begin
   -- ================================================
   -- Determiner l'annee de traitement N et N+1
   -- ================================================
   l_annee0 := to_char(add_months(sysdate,-1),'YYYY');
   l_annee1 := substr(to_char(to_number(l_annee0,'FM9999') + 1,'FM9999'),3,2);
   return l_annee1;
end;

      function CF_CLIENTFormula return Char is
begin
  IF :P_PARAM6 = 'OPER'Then
  	return 'Opérationnel';
  Else
  	return 'Payeur';
  End If;
end;
