function.textSource:
      function AfterPForm return boolean is

begin
:P_PERIME:=PACK_GLOBAL.LIRE_PERIME(:P_GLOBAL);

	--srw.message(1,:p_perime);

  return (true);
end;

      function R_POSTEFormatTrigger return boolean is
reste number;
begin
reste := mod(:CS_1,2);
if reste=0 then
-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='white';
 srw.set_attr (0,srw.attr);
end if;
  return (TRUE);
end;

      function R_dpg1FormatTrigger return boolean is
reste number;
begin
reste := mod(:CS_2,2);
if reste=0 then
-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='white';
 srw.set_attr (0,srw.attr);
end if;
  return (TRUE);
end;
