      function AfterPForm return boolean is


begin
  
    
  :P_NUMSEQ := pack_cout_standard_ke.INSERT_TMP_COPI_SYNTHESE(null,:P_PARAM6 , null,2); 
  
  return (TRUE);
end;

      function CF_REAL_ARBFormula return Number is
begin
	if (:EU_ARBDECIDES <> 0)
	then
  	return((round(:EU_REALISES)/round(:EU_ARBDECIDES))*100);
	else
		return 0;
	end if;
end;

      function CF_REAL_ARB_CANTFormula return Number is
begin
	if ((:EU_ARBDECIDES+:EU_CANTDECIDES) <> 0)
	then
  	return((round(:EU_REALISES)/(round(:EU_ARBDECIDES)+round(:EU_CANTDECIDES)))*100);
	else
		return 0;
	end if;
end;

      function CF_REAL_ARB_CLISIGLEFormula return Number is
begin
	if (:SumEU_ARBDECIDESPerCLISIGLE <> 0)
	then
	  return((round(:SumEU_REALISESPerCLISIGLE)/round(:SumEU_ARBDECIDESPerCLISIGLE))*100);
	else
		return 0;
	end if;
	
end;

      function CF_REAL_ARB_CANT_CLISIGLEFormu return Number is
begin
	if ((:SumEU_ARBDECIDESPerCLISIGLE+:SumEU_CANTDECIDESPerCLISIGLE) <> 0 )
	then 
   return((round(:SumEU_REALISESPerCLISIGLE)/(round(:SumEU_ARBDECIDESPerCLISIGLE)+round(:SumEU_CANTDECIDESPerCLISIGLE)))*100);
	else
		return 0;
	end if;
end;

      function CF_REAL_ARB_TOTALMETIERFormula return Number is
begin
	if (:Total_EU_ARB <> 0)
	then
	  return((round(:Total_EU_REAL)/round(:Total_EU_ARB))*100);
	else
		return 0;
	end if;
	
end;

      function CF_REAL_ARB_CANT_TOTALMETIERFo return Number is
begin
	if ((:Total_EU_ARB+:Total_EU_CANT) <> 0)
	then
    return((round(:Total_EU_REAL)/(round(:Total_EU_ARB)+round(:Total_EU_CANT)))*100);
	else
		return 0;
	end if;
	
end;

      function CF_TOTALANNEE_REAL_ARBFormula return Number is
begin
	
	if ((:SumEU_ARBDECIDESPerANNEE+:SumEU_CANTDECIDESPerANNEE) <> 0)
	then
		 return((round(:SumEU_REALISESPerANNEE)/(round(:SumEU_ARBDECIDESPerANNEE)+round(:SumEU_CANTDECIDESPerANNEE)))*100);
	else
		return 0;
	end if;
end;

      function CF_TOTALANNE_REAL_ARBFormula return Number is
begin
	if (:SumEU_ARBDECIDESPerANNEE <> 0)
	then	
  	return((round(:SumEU_REALISESPerANNEE)/round(:SumEU_ARBDECIDESPerANNEE))*100);
