        l_compteur := l_compteur+1;
       end if;
      END LOOP;      

    :p_from := :p_from || ')';
    
    if (:p_from = '()' or length(:p_from)>=30000) then
		:p_where := 'from synthese_fin sf,
										client_mo cli,
										ligne_bip bip,
										centre_activite ca, 
										datdebex d,
										dossier_projet dosp,
										proj_info pi
								 where bip.PID = sf.PID
									  and bip.CLICODE = cli.CLICODE
									  and sf.CODCAMO = ca.CODCAMO
									  and sf.ANNEE = to_char(d.MOISMENS,''YYYY'')
									  AND bip.dpcode = dosp.dpcode
									  and bip.icpi||'''' = pi.ICPI
									  and sf.CADA = pi.CADA '
										||p_where_perim;
    else
  	:p_where := 'from synthese_fin sf,
										client_mo cli,
										ligne_bip bip,
										centre_activite ca, 
										datdebex d,
										dossier_projet dosp,
										proj_info pi,
										'||:p_from||' liste_ca	
								 where bip.PID = sf.PID
									  and bip.CLICODE = cli.CLICODE
									  and sf.ANNEE = to_char(d.MOISMENS,''YYYY'')
									  AND bip.dpcode = dosp.dpcode
									  and bip.icpi||'''' = pi.ICPI
									  and sf.CADA = pi.CADA 
									  and ca.codcamo = liste_ca.CODCAMO
									  and sf.cada = liste_ca.codcamo '
										||p_where_perim;
    end if;	

 	  --srw.message(1,:P_WHERE);
END IF;
  return (TRUE);
end ;

      function R_StatutFormatTrigger return boolean is
begin
  if (:dc_statut <> 'A')
  then
  	return (TRUE);
  else
  	return (FALSE);
  end if;
end;

      function R_Statut1FormatTrigger return boolean is
begin
  if (:dc_statut = 'A')
  then
  	return (TRUE);
  else
  	return (FALSE);
  end if;
end;

      function R_Direction1FormatTrigger return boolean is
	reste	NUMBER;
begin
	reste := mod(:CS_NUM,2);
	if reste=0 then
		-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
		srw.attr.bfcolor :='white';
		srw.set_attr (0,srw.attr);
	end if;
  return (TRUE);
end;

      function M_Direction1FormatTrigger return boolean is
begin
  IF(:FIXE_1 = 'REF') THEN
  	 return (FALSE);
	else
  	 return (TRUE);
  end if ;
end;

      function CF_1Formula return Number is
  l_sum number(8,2);
begin
  l_sum := :dc_dir_factint_moisf - :dc_dir_rectif_moisf;
  return(l_sum);
end;

