function.textSource:
      function CF_ecartFormula return Number is
begin
  

	return (:CF_MONTH_FACT - :CF_CONSOHT_RESS);
  

end;

      function CF_factFormula return Number is
begin
  IF (:dc_lmonth IS NULL) THEN
     IF (:dc_montant_mens IS NULL) THEN
    return (0-:dc_cousag);
  ELSIF (:dc_montant_mens IS NOT NULL) THEN
    return (0-:dc_montant_mens);
  END IF;
else return(NULL);
  END IF;
  return(NULL);

end;

      function CF_MONTH_FACTFormula return Number is
	l_res NUMBER(15, 2);
begin
  IF (:dc_typfact = 'A')
  THEN
	RETURN( - :dc_lmonth);
  ELSE
	RETURN(  :dc_lmonth);
  END IF;
end;

      function R_MOISPRESTFormatTrigger return boolean is
begin
  

  return (TRUE);
end;

      function F_cousagFormatTrigger return boolean is
begin
  IF ( :CS_CPT_LIGNE > 1 ) THEN
  	SRW.SET_FIELD_NUM(0, 0);
RETURN(FALSE);
  
  ELSE
  	return (TRUE);
  END IF;
end;

      function F_CUSAGFormatTrigger return boolean is
begin
  IF ( :CS_CPT_LIGNE > 1 ) THEN
  	SRW.SET_FIELD_NUM(0, 0);
		RETURN(FALSE);
   ELSIF (:dc_montant_mens is not null) then
  	RETURN(FALSE);
  	ELSE
  	return (TRUE);
  END IF;
end;

      function F_COUTFormatTrigger return boolean is
begin
   IF ( :CS_CPT_LIGNE > 1 ) THEN
  	SRW.SET_FIELD_NUM(0, 0);
RETURN(FALSE);
	ELSIF (:dc_montant_mens is not null) then
  	RETURN(FALSE);
  ELSE
  	return (TRUE);
  END IF;
end;

      function CF_CONSOHT_RESSFormula return Number is
begin
    IF ( :CS_CPT_LIGNE > 1 ) THEN
	RETURN(0);
    ELSIF (:dc_montant_mens is null) THEN
  	RETURN(:dc_cousag*:dc_cout);
    ELSIF (:dc_montant_mens is not null) THEN
    RETURN(:dc_montant_mens);	
    END IF;
    
    
end;

      function AfterPForm return boolean is

 l_param   VARCHAR2(40);
 l_perim_me	VARCHAR2(1000) := '';
 l_menutil	VARCHAR2(50);
 
begin

	l_menutil := pack_global.lire_globaldata(:P_GLOBAL).menutil;
	
	-- Filtre du perim ME uniquement sur menu "Responsables etudes"
	IF NVL(l_menutil,'-1') = 'ME' THEN
			
			-- Filtre du perim ME
		l_perim_me := pack_global.lire_perime(:P_GLOBAL);		
		:P_WHERE :=' AND r.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 
	
	END IF;

  -- ================================================
   -- Soustraire les eventuels $ ou ' ' dans p_param6
   -- ================================================
   IF NVL(LENGTH(:P_PARAM6), 0)!= 0 then 
        l_param := rtrim(rtrim(LPAD(:p_param6, 7, '0'),'*')) ;
	:p_where := :p_where || ' and  TO_CHAR(r.codsg, ''FM0000000'') like ''' || l_param ||'%'' ';
   END IF;

   IF :P_PARAM7='N1' THEN
	:p_where := :p_where ||' and trunc(r.lmoisprest,'''||'YEAR'||''')=ADD_MONTHS(d.datdebex,-12)';
   ELSE
	:p_where := :p_where ||' and trunc(r.lmoisprest,'''||'YEAR'||''')=d.datdebex';
   END IF; 

  return (TRUE);
end;

      function CF_1_ecart_htrFormula return Number is


l_cout_htr number(12,2);
begin

	
l_cout_htr := :CF_ecart + (:CF_tva  - :CF_taux_recup/100 * :CF_tva )/100*(:CF_ecart);
return l_cout_htr;


end;

      function CF_1_fact_htrFormula return Number is

l_cout_htr number(12,2);
begin

	
l_cout_htr := :CF_fact + (:CF_tva  - :CF_taux_recup/100 * :CF_tva )/100*(:CF_fact);
return l_cout_htr;


end;

      function CF_tvaFormula return Number is

l_tva number(9,2);
begin
  	select tva into l_tva
	from tva where datetva = (select max(datetva) from tva);
   
return(l_tva);
end;

      function CF_taux_recupFormula return Number is
taux_recup number(9,2);
begin
SELECT taux into taux_recup 
FROM taux_recup, datdebex
where annee = to_number(to_char(datdebex,'yyyy')) 
and filcode ='01';
return taux_recup;  
end;

