function.textSource:
      -------------------------------------------------------------------
--             function FUNC_GET_FILIALE
--
--    Renvoie en la filiale en fonction du code
--    p_filcode IN : Code filiale 
--    Retour       : Code filiale et libellé assicié sous la forme 
--                   code + ' ' + libellé
-------------------------------------------------------------------
FUNCTION FUNC_GET_FILIALE(p_filcode in varchar2) RETURN varchar2 IS
   l_text filiale_cli.FILSIGLE%TYPE;
BEGIN

   if NVL(LENGTH(p_filcode), 0) != 0 then 
      SELECT filsigle into l_text from filiale_cli 
      WHERE rtrim(FILCODE) = p_filcode;
      return  p_filcode || ' ' ||l_text;
  else
      return '';
  end if;
END;

      function CF_POLEFormula return VARCHAR2 is
begin
    return pack_utile.f_get_deppole(:dc_fdeppole);
end;

      function CF_FILIALEFormula return VARCHAR2 is
begin
 return pack_utile.f_get_filiale(:P_PARAM3);
end;

      function CF_TOT_LIGNEFormula return Number is
begin
  -- calcul du total pour la ligne 
  return (:cs_ct0 + :cs_ct1 + :cs_ct7 + :cs_ct15 + :cs_ct30 + :cs_ct60);
end;

      function M_1FormatTrigger return boolean is
begin
  if (:CS_CT_POLE != 1) then 
     return (TRUE);
  else
     return (FALSE);
  end if;
end;

      function CF_LIBDEPFormula return VARCHAR2 is
begin
   return pack_utile.f_get_dep(:dc_fdep);
end;

      function CF_codcomptaFormula return VARCHAR2 is
begin
  
   -- On tronque le 1er caractère utilisé pour l'ordre
   return substr(:dc_fcodcompta,2);
 
end;

      function AfterPForm return boolean is
begin

  -- Passage de paramètres 
   :p_datdebformat := :p_param6;   -- sert à l'affichage dans le titre
   :p_datfinformat := :p_param7;   -- sert à l'affichage dans le titre
 
   :p_datdebut := to_char(to_date(:p_param6,'DD/MM/YYYY'),'DDMMYYYY');
   :p_datfin   := to_char(to_date(:p_param7,'DD/MM/YYYY'),'DDMMYYYY');

 IF (:P_PARAM5 !='0')  THEN 
	:P_WHERE := ' and to_char(fac.FCENTREFRAIS) = ''' || :P_PARAM5 ||'''';
 ELSE
	:P_WHERE :=  ' and 1=1 ';
  END IF;


--srw.message(1,:p_where);
   return (TRUE);
end;

      function CF_TOT_POLEFormula return Number is
begin
      return (:cs_pole_ct0 + :cs_pole_ct1 + :cs_pole_ct7 + :cs_pole_ct15 + :cs_pole_ct30 + :cs_pole_ct60);
end;

      function CF_TOT_DEPFormula return Number is
begin
      return (:cs_dept_ct0 + :cs_dept_ct1 + :cs_dept_ct7 + :cs_dept_ct15 + :cs_dept_ct30 + :cs_dept_ct60);
end;

      function F_CFRAIS1FormatTrigger return boolean is
Text varchar2(60);
begin
  if NVL(LENGTH(:P_PARAM5), 0)!= 0 then 
	 Select LIBCFRAIS into Text from centre_frais where to_char(codcfrais) = :P_PARAM5;
	 Text := :P_PARAM5 || ' - ' ||Text;
	 SRW.SET_FIELD_CHAR(0,Text);
  end if;
  return (TRUE);
end;
