      function AfterPForm return boolean is
 l_param   VARCHAR2(40);
 l_liste   VARCHAR2(255);
begin
 if :P_PARAM6='Toute' then
 l_liste :=pack_global.lire_globaldata(:P_GLOBAL).chefprojet;
:p_where := :p_where || ' and c.pid in (
				select pid
				from ligne_bip l, datdebex d
				where INSTR('''||l_liste||''',TO_CHAR(l.pcpi,''FM00000''))>0
				minus 
				select pid
				from ligne_bip l, datdebex d
				where INSTR('''||l_liste||''',TO_CHAR(l.pcpi,''FM00000''))>0
				and ((topfer=''O'' and TRUNC(adatestatut,''YEAR'')<TRUNC(d.datdebex,''YEAR'')  )
				or 
				(astatut  in (''A'',''D'',''C'')
	 				and TRUNC(adatestatut,''YEAR'')<TRUNC(d.datdebex,''YEAR'') )
				)
			)';

 else
 :p_where := :p_where || ' and c.pid=:P_PARAM6 ';
 end if;

   --srw.message(1,:P_where);  
  return (TRUE);
end;

      function F_IDENT1FormatTrigger return boolean is
begin

  return (TRUE);
end;

      function M_6FormatTrigger return boolean is
reste number;
begin
reste := mod(:CS_NUM,2);
if reste=0 then
    srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='white';
 srw.set_attr (0,srw.attr);
end if;
  return (TRUE);
end;

      function CF_1Formula return Number is
total number(8,2);
begin
  total := NVL(:MOIS_1,0)+NVL(:MOIS_2,0)+ NVL(:MOIS_3,0) +
           NVL(:MOIS_4,0) + NVL(:MOIS_5,0) + NVL(:MOIS_6 ,0)+
           NVL(:MOIS_7,0) + NVL(:MOIS_8,0) + NVL(:MOIS_9,0) +
           NVL(:MOIS_10,0) + NVL(:MOIS_11,0) + NVL(:MOIS_12,0);

return total;
end;
