      ------------------------------------------------------------------------------------------
-- Rôle : Construire un Where conditionnel. La chaine construite sera affectée au parametre
-- de P_WHERE. 
------------------------------------------------------------------------------------------
function AfterPForm return boolean is

	l_param varchar2(10);
	
begin

  if :P_PARAM3 is not null then 
	:p_where := ' and C.FILCODE = ''' || :P_PARAM3 ||'''';
   end if;

  if :P_PARAM7 is not null then 
	:p_where := :p_where || ' and C.SOCCONT = ''' || :P_PARAM7 ||'''';
  end if;

 
-- -------------
  if :P_PARAM8 is not null then 
  	
   IF NVL(LENGTH(LPAD(:P_PARAM8, 7, '0')), 0) != 0 then 
   		-- ajout de '0' a gauche sur 7 caracteres + suppression des '*'
      l_param := rtrim(rtrim(LPAD(:P_PARAM8, 7, '0'),'*')) ;
			:p_where := :p_where || ' and  TO_CHAR(C.codsg, ''FM0000000'') like ''' || l_param || '%'' ';
   END IF;  	
  :P_PARAM8 := l_param	;

  end if;
  
  if :P_PARAM9 is not null then 
	:p_where := :p_where || ' and L.IDENT = ' || :P_PARAM9;
  end if;
  if :P_PARAM10 is not null then
     if :P_PARAM10 in ('R','M','F') then
	:p_where := :p_where || ' and C.CTYPFACT = ''' || :P_PARAM10 ||'''';
     end if;
  end if;

  -- sélectionner les lignes de contrats qui ont vécu au moins pendant une partie de leur vie dans
  -- la période saisie. 
  if (:P_PARAM11 is not null) and (:P_PARAM12 is not null) then 

-- modif qualif interne 5/5/00

  	:p_where := :P_where || ' and C.CDATDEB <= to_date(''' || :P_PARAM12 || ''',''dd/mm/yyyy'')';
  	:p_where := :P_where || ' and C.CDATFIN >= to_date(''' || :P_PARAM11 || ''',''dd/mm/yyyy'')';
  end if;

  if :P_PARAM13 is not null then 
	:p_where := :p_where || ' and  C.CCOUTHT >= ' ||
                    to_char(TO_NUMBER(:P_PARAM13, '999999999999D99', 'NLS_NUMERIC_CHARACTERS = '',.'''));
  end if;
  
  IF (:P_PARAM5 !='0')  THEN 
	:p_where := :p_where || ' and to_char(C.CCENTREFRAIS) = ''' || :P_PARAM5 ||'''';
  END IF; 

  :p_where := :p_where || ' ';
   --srw.message(1,:p_where);
  return (TRUE);

end;

      ------------------------------------------------------------------------------------------
-- Rôle : Construire le contenu du champ F_CritereSelection qui affiche criteres de 
--        seletion (rappel des parametres d'entrées).
------------------------------------------------------------------------------------------
function BeforeReport return boolean is
  Text varchar2(100);
  Text2 varchar2(100);
begin

  if :P_PARAM7 is not null then 
	select SOCLIB into Text from societe where rtrim(SOCCODE) = :P_PARAM7;
      :P_cds_Soc:= :P_PARAM7 || ' ' || Text;
  end if;
    
-- affichage de DPG ou du dep/pole ou du département 
  IF :P_PARAM8 IS NOT NULL THEN      
     
     IF NVL(LENGTH(:P_PARAM8), 0) = 7 THEN               
     	  SELECT LIBDSG INTO Text FROM struct_info WHERE CODSG = TO_NUMBER(:P_PARAM8);
        :P_cds_TitreDPG := 'Dép/pôle/gr  :';
        :P_cds_DPG := :P_PARAM8  || ' ' || Text;     
     ELSIF ( NVL(LENGTH(:P_PARAM8), 0) >=5 AND NVL(LENGTH(:P_PARAM8), 0) <= 6 ) THEN                     
        SELECT sigdep, sigpole INTO Text,Text2 FROM struct_info 
        WHERE coddep = substr(:P_PARAM8,1,3)
          and codpole = substr(:P_PARAM8,4,2)
          and rownum <=1;
        :P_cds_TitreDPG := 'Dép/pôle     :' ;
        :P_cds_DPG := :P_PARAM8  || ' ' || Text || '/' || Text2;
     ELSIF ( NVL(LENGTH(:P_PARAM8), 0) >= 3 AND NVL(LENGTH(:P_PARAM8), 0) <= 4 ) THEN 
        SELECT sigdep INTO Text FROM struct_info 
        WHERE coddep = substr(:P_PARAM8,1,3)
          and rownum <=1;
        :P_cds_TitreDPG := 'Dép          :';
        :P_cds_DPG := :P_PARAM8  || ' ' || Text;
     END IF;

  END IF;

  IF :P_PARAM9 is not null then 
        :P_cds_Ress := :P_PARAM9;
  END IF;

  IF :P_PARAM10 is not null then 
        :P_cds_Typfact := :P_PARAM10;
  END IF;
  
  IF (:P_PARAM11 is not null) AND (:P_PARAM12 is not null) THEN 
        :P_cds_Periode := :P_PARAM11 || ' AU ' || :P_PARAM12;
  END IF;

  
  IF (:P_PARAM13 is not null) THEN
        :P_cds_MtMin := to_char(TO_NUMBER(:P_PARAM13, '999999999999D99', 'NLS_NUMERIC_CHARACTERS = '',.'''));
  END IF;

  RETURN (TRUE);

end;

      FUNCTION RtrimEtoile(P_Text In VARCHAR2) RETURN varchar2 IS
  meta VARCHAR2(1);
  L_Text varchar2(7);
BEGIN
  L_Text := P_Text;
  meta := substr(P_Text, NVL(length(P_Text), 0),1);
  if (meta = ' ') or (meta = '*') then
      L_Text := rtrim(P_Text,meta);   
  end if;
  return(L_Text);
END;

      function F_CFRAISFormatTrigger return boolean is
Text varchar2(60);
begin
  if NVL(LENGTH(:P_PARAM5), 0)!= 0 then 
	 Select LIBCFRAIS into Text from centre_frais where to_char(codcfrais) = :P_PARAM5;
	Text := :P_PARAM5 || ' - ' ||Text;
	 SRW.SET_FIELD_CHAR(0,Text);
  end if;
  return (TRUE);
end;

