      ------------------------------------------------------------------------------------------
-- Rôle : Construire un Where conditionnel. La chaine construite sera affectée au parametre
-- de P_WHERE. 
------------------------------------------------------------------------------------------
function AfterPForm return boolean is
Text varchar2(7);
begin
  :p_where := '';
  IF :P_PARAM6 IS NOT NULL THEN 
	:p_where := :p_where || ' and f.socfact = ''' || :P_PARAM6 ||'''';
  END IF;
    
  IF (:P_PARAM7 IS NOT NULL) and (:P_PARAM8 IS NOT NULL) THEN 
	:p_where := :P_where || ' and f.FENRCOMPTA >= TO_DATE(''' || :P_PARAM7 || ''',''dd/mm/yyyy'')';
        :p_where := :P_where || ' and f.FENRCOMPTA <= TO_DATE(''' || :P_PARAM8 || ''',''dd/mm/yyyy'')';
  END IF;

  IF (:P_PARAM9 IS NOT NULL) and (:P_PARAM10 IS NOT NULL) THEN 
	:p_where := :P_where || ' and l.LMOISPREST >= TO_DATE(''' || :P_PARAM9 || ''',''dd/mm/yyyy'')';
        :p_where := :P_where || ' and l.LMOISPREST <= TO_DATE(''' || :P_PARAM10 || ''',''dd/mm/yyyy'')';
  END IF;

-- -------------
  IF :P_PARAM11 IS NOT NULL THEN 
     Text:= RtrimEtoile(LPAD(:P_PARAM11, 7, '0'));
     :P_PARAM11 := Text;
      
     IF NVL(LENGTH(Text), 0)= 7 THEN               
      	:p_where := :p_where || ' and f.fdeppole = ' || Text;
     ELSIF NVL(LENGTH(Text), 0)= 5 THEN                     
        :p_where := :p_where || ' and SUBSTR(to_char(f.fdeppole, ''FM0000000''),1,5) = ''' || Text ||'''';
      ELSIF NVL(LENGTH(Text), 0)= 3 THEN 
        :p_where := :p_where || ' and SUBSTR(to_char(f.fdeppole, ''FM0000000''),1,3) = ''' || Text ||'''';
     END IF;

  END IF;


  IF (:P_PARAM12 IS NOT NULL) AND (:P_PARAM6 IS NULL) THEN 
      IF :P_PARAM12 = 'O' THEN  
         :p_where := :p_where || ' and  S.SOCNAT = ''A''' ;
      ELSIF :P_PARAM12 = 'N' THEN 
         :p_where := :p_where || ' and ((S.SOCNAT IS NULL) or (S.SOCNAT != ''A''))';
         --:p_where := :p_where || ' and S.SOCNAT != ''A''';
      ELSIF :P_PARAM12 = ' ' THEN 
         null;
      END IF;

  END IF;

  IF :P_PARAM13 IS NOT NULL THEN 
	:p_where := :p_where || ' and l.lcodcompta = ''' || :P_PARAM13 ||'''';
  END IF;

  IF :P_PARAM14 IS NOT NULL THEN        
        :p_where := :p_where || ' and f.fmontht >= ' || :P_PARAM14;
        --:p_where := :p_where || ' and l.lmontht >= ' || :P_PARAM14;
  END IF;

  IF :P_PARAM15 IS NOT NULL THEN 
	:p_where := :p_where || ' and f.num_expense = ''' || :P_PARAM15 ||'''';
  END IF;

  :p_where2 := :p_where; 

IF (:P_PARAM5 !='0') THEN 
	:p_where := :p_where || ' and to_char(f.FCENTREFRAIS) = ''' || :P_PARAM5 ||'''';
        :p_where2 := :p_where; 
  END IF;

  IF :P_PARAM3 IS NOT NULL THEN 
	:p_where := :p_where || ' and c.filcode = ''' || :P_PARAM3 ||'''';
  END IF;

  :p_where := :p_where || ' ';
  :p_where2 := :p_where2 || ' ';
   --srw.message(1,:p_where);
  -- --srw.message(1,:p_where2);
  RETURN (TRUE);
END;

      FUNCTION RtrimEtoile(P_Text In VARCHAR2) RETURN VARCHAR2 IS
  meta VARCHAR2(1);
  L_Text VARCHAR2(7);
BEGIN
  L_Text := P_Text;
  meta := SUBSTR(P_Text, NVL(length(P_Text), 0),1);
  IF (meta = ' ') OR (meta = '*') THEN
      L_Text := RTRIM(P_Text,meta);   
  END IF;
  RETURN(L_Text);
END;

      function BeforeReport return boolean is
  l_Filtre varchar2(100);  -- Sert à construire les lignes de la rubrique critères de selection
  l_Text varchar2(100);
  l_Text2 varchar2(100);
begin
  IF :P_PARAM6 IS NOT NULL THEN 
      SELECT soclib INTO l_Text FROM societe WHERE RTRIM(soccode) = :P_PARAM6;
      :P_cds_Soc:= :P_PARAM6 || ' ' || l_Text;
  END IF;
    
  IF (:P_PARAM7 IS NOT NULL) AND (:P_PARAM8 IS NOT NULL) THEN 
      :P_cds_PeriodeEnreg := :P_PARAM7 || ' AU ' || :P_PARAM8;
  END IF;

  IF (:P_PARAM9 IS NOT NULL) AND (:P_PARAM10 IS NOT NULL) THEN 
      :P_cds_PeriodePrest := :P_PARAM9 || ' AU ' || :P_PARAM10;

  END IF;
  
-- affichage de DPG ou du dep/pole ou du département 
IF :P_PARAM11 IS NOT NULL THEN           
     IF NVL(LENGTH(:P_PARAM11), 0)= 7 THEN               
     	  SELECT LIBDSG INTO l_Text FROM struct_info WHERE CODSG = TO_NUMBER(:P_PARAM11);
        :P_cds_TitreDPG := 'Dép/pôle/gr : ';
        :P_cds_CODDPG := LPAD(:P_PARAM11, 7, '0') || ' ' || l_Text;     
     ELSIF NVL(LENGTH(:P_PARAM11), 0)= 5 THEN                     
        SELECT sigdep, sigpole INTO l_Text,l_Text2 FROM struct_info 
        WHERE coddep = substr(LPAD(:P_PARAM11, 5, '0'), 1, 3)
        and codpole = substr(LPAD(:P_PARAM11, 5, '0'),4,2)
        and rownum <=1;

        :P_cds_TitreDPG := 'Dép/pôle : ' ;
        :P_cds_CODDPG := LPAD(:P_PARAM11, 5, '0') || ' ' || l_Text || '/' || l_Text2;

     ELSIF NVL(LENGTH(:P_PARAM11), 0)= 3 THEN 
        SELECT sigdep INTO l_Text FROM struct_info 
        WHERE coddep = substr(LPAD(:P_PARAM11, 3, '0'),1,3)
        and rownum <=1;

        :P_cds_TitreDPG := 'Dép : ';
        :P_cds_CODDPG := LPAD(:P_PARAM11, 3, '0')  || ' ' || l_Text;
     END IF;

  END IF;


  IF (:P_PARAM12 IS NOT NULL) AND (:P_PARAM6 IS NULL) THEN  -- et si codsoc n'est pas saisie
     IF :P_PARAM12='O' THEN 
        :P_cds_PourSoc := 'Pour les sociétés agréées';        
     ELSIF :P_PARAM12='N' THEN
        :P_cds_PourSoc := 'Pour les sociétés non agréées';
     END IF;
  END IF;

  IF :P_PARAM13 IS NOT NULL THEN 
      :P_cds_CodCompt := :P_PARAM13;
  END IF;

  IF :P_PARAM14 IS NOT NULL THEN 
      :P_cds_MtMin := :P_PARAM14;
  END IF;

  return (TRUE);
end;

      function F_CFRAISFormatTrigger return boolean is
Text varchar2(60);
begin
  if NVL(LENGTH(:P_PARAM5), 0)!= 0 then 
	 Select LIBCFRAIS into Text from centre_frais where to_char(codcfrais) = :P_PARAM5;
	 Text := :P_PARAM5 || ' - ' ||Text;
	 SRW.SET_FIELD_CHAR(0,Text);
  end if;
  return (TRUE);
end;

      function F_FilialeFormatTrigger return boolean is
Text filiale_cli.filsigle%type;
begin
  if NVL(LENGTH(:P_PARAM3), 0)!= 0 then 
	 Select FILSIGLE into Text from filiale_cli where rtrim(FILCODE) = rtrim(:P_PARAM3);
	 Text := :P_PARAM3 || ' ' ||Text;
	 SRW.SET_FIELD_CHAR(0,Text);
  end if;

 
  return (TRUE);
end;

      function F_FPROVSEGL3FormatTrigger return boolean is
begin
  IF :FPROVSEGL2=1 THEN
	 SRW.SET_FIELD_CHAR(0,'OUI');
  ELSE
	SRW.SET_FIELD_CHAR(0,'NON');
  end if;
  return (TRUE);
end;

