      function AfterPForm return boolean is
	l_global pack_global.GlobalData;
	tmp varchar2(5000);
	lib	varchar2(255);
	l_menu VARCHAR2(25);
	pos integer;
begin
	
	l_global := pack_global.lire_globaldata(:P_GLOBAL);
	
	:P_PERIME := l_global.perime;
	:P_PARAM4 := l_global.direction1;
	--srw.message(1,'PERIM_MO_DEFAUT : ' || :P_PARAM4);
  :p_user_nom := :P_PARAM6;
  :p_user_prenom := :P_PARAM7;
  
  :p_user_id := l_global.idarpege;  
  
  :P_PARAM2 := l_global.codpole;
  :P_PERIMCLI := l_global.perimcli;
  
  /* QC 1187 suite fiche 1176 */
  :P_PERIMMO := l_global.perimo;
  
  :P_CHEF_PROJET := l_global.chefprojet;
  --srw.message(1,'CP : ' || :P_CHEF_PROJET);
  :P_APP := l_global.appli;
  :P_DOS_PROJ := l_global.doss_proj;
  --srw.message(2,'dossier projet : ' || :P_DOS_PROJ);
  :P_PROJET := l_global.projet;
  :P_CA_FI := l_global.ca_fi;
  :P_CA_PAYEUR := l_global.ca_payeur;
  :P_CA_DA := l_global.ca_da;
  
  :P_CA_SUIVINV := :P_PARAM10;
  :P_CENTRE2FRAIS := :P_PARAM5;
  
  --:P_PARAM11 := '35,82';  
  if ((:P_PARAM11 = '') or (:P_PARAM11 is null))	then
      :P_WHERE := ' where 1=0 ';
  else    	
  		tmp := :P_PARAM11;
  		tmp := REPLACE(tmp,',',''',''');
  		tmp := ''''||tmp||'''';
  		:P_WHERE := ' where to_char(codcfrais) in ('||tmp||') '; 
  end if ;
  
  --srw.message(1,:P_WHERE);
  
  
  if :P_DOS_PROJ = 'TOUS' or :P_DOS_PROJ is null then
  	:P_DP :=  '';
  else
  		tmp := :P_DOS_PROJ;
  		tmp := REPLACE(tmp,',',''',''');
  		tmp := ''''||tmp||'''';
  		:P_DP :=  ' WHERE to_char(dpcode,''FM00000'') in ('||tmp||') ';	
  		
  end if;


  -- construction des requetes liées aux menus
  tmp := :P_PARAM8;
  IF (length(tmp) > 0) THEN
    tmp := REPLACE(:P_PARAM8,'''','''''');
    :P_MENUS := ' UNION select ''';
	  LOOP
	  	pos := instr(tmp,';');
	  	if (pos <= 0)
	  	then
				:P_MENUS := :P_MENUS || tmp || ''' from dual';  		
	  		exit;
	  	end if;
	  	-- ajout p_menus
	  	
	  	:P_MENUS := :P_MENUS || substr(tmp,0,pos-1) || ''' from dual';
	  	
	  	--on reduit tmp
	  	tmp := substr(tmp,pos+1,length(tmp));  	  	
	  	:P_MENUS := :P_MENUS || ' UNION select ''';
	  END LOOP;
  END IF ;
   --srw.message(1,'p_menu : '||:P_MENUS);
  tmp := :P_PARAM9;
  if (length(tmp) > 0)
  then 
		tmp := REPLACE(:P_PARAM9,'''','''''');
  	
	  :P_SSMENUS := ' UNION select ''';
	  LOOP
	  	pos := instr(tmp,';');
	  	
	  	if (pos <= 0)
	  	then
				:P_SSMENUS := :P_SSMENUS || tmp || ''' from dual';  		
	  		exit;
	  	end if;
	  	
	  	:P_SSMENUS := :P_SSMENUS || substr(tmp,0,pos-1) || ''' from dual';
	  	
	  	--on reduit tmp
	  	tmp := substr(tmp,pos+1,length(tmp));  	  	
	  	:P_SSMENUS := :P_SSMENUS || ' UNION select ''';
	  	
	  	--srw.message(1,'p_sous_menu : '||:P_SSMENUS);
	  END LOOP;
  end if;
  
  --srw.message(1,'p_perim_cli : '||:P_PERIMCLI);
  return (TRUE);
  
  
end;

      function R_PERIME1FormatTrigger return boolean is
	reste	NUMBER;
begin
	if (:P_PERIME = '00000000000')
  then
		return (FALSE);  	
	end if;
  
	
	reste := mod(:CS_NUM_PERIME,2);
	--reste:=1;
	if reste=1 then
		-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
		srw.attr.bfcolor :='white';
		srw.set_attr (0,srw.attr);
	end if;
  return (TRUE);
end;

