      function CF_DPG_DEFAUT_LIBFormula return CHAR is
lib struct_info.libdsg%TYPE;
begin
  if 	( (:P_PARAM2 = '') or (:P_PARAM2 is null))
	then
			return null ;
  else		
  	BEGIN
		select libdsg into lib
		from struct_info
		where :P_PARAM2 = codsg;
		
  	EXCEPTION
  		WHEN NO_DATA_FOUND THEN
  			return 'DPG inconnu ou générique' ;
		END;
	  return lib;
  end if;	  
end;

      function CF_CLIENT_DEFAUT_SIGLEFormula return Char is
sigle client_mo.clisigle%TYPE;
begin
  if 	((:P_PARAM4 = '88888') or (:P_PARAM4 = '') or (:P_PARAM4 is null))
	then
			return null ;
  else	
  	begin
	     select clisigle into sigle
	     from client_mo
	     where :P_PARAM4 = clicode;
	  
	  EXCEPTION
  		WHEN NO_DATA_FOUND THEN
  			return null ;
		END;
	  
	  return sigle;
	     
	end if ;  
end;

