      function CF_CENTRE2FRAIS_LIBFormula return Char is
lib centre_frais.libcfrais%TYPE;
begin
	  if (:P_CENTRE2FRAIS is null or :P_CENTRE2FRAIS='')
		then
			return null ;
		else	
			select libcfrais into lib
			from centre_frais
			where codcfrais = :P_CENTRE2FRAIS;
	
			return lib;
		end if;
  
end;

