      function BeforeReport return boolean is
virgules NUMBER;
i NUMBER;
pos NUMBER;
pos1 NUMBER;
c_ident VARCHAR(5000);
num NUMBER;
begin
	/*
	* Récupération des chefs de projet inconnus
	*
	*/
  virgules := ( (LENGTH(:P_CHEF_PROJET) - LENGTH(REPLACE(:P_CHEF_PROJET,',',NULL)) ) / NVL(LENGTH(','),1) ) ;
  	i:=0;
  	While i < virgules+1
      Loop
      	if (virgules=0) then
      			c_ident := :P_CHEF_PROJET;
        elsif (i=0)then
        		pos := INSTR( :P_CHEF_PROJET, ',', 1, 1);
        		c_ident := substr( :P_CHEF_PROJET, 1, pos-1);
        elsif (i=virgules) then
        		pos := INSTR( :P_CHEF_PROJET, ',', 1, i);
        			c_ident := substr( :P_CHEF_PROJET, pos+1, LENGTH(:P_CHEF_PROJET)-1);
        else
        		pos := INSTR( :P_CHEF_PROJET, ',', 1, i);
        		pos1 := INSTR( :P_CHEF_PROJET, ',', 1, i+1);
        		c_ident := substr( :P_CHEF_PROJET, pos+1, pos1-pos-1);
        end if;
        
        select count(*) into num
        from ressource res
        where INSTR(c_ident, to_char(res.ident,'FM00000')) > 0;

        if(num=0 or LENGTH(c_ident)>5)then

        	if(:C_CP_INCONNUS='' or :C_CP_INCONNUS is null)then
        		:C_CP_INCONNUS := :C_CP_INCONNUS || c_ident;
        	else
        		:C_CP_INCONNUS := :C_CP_INCONNUS || ', ' || c_ident;
        	end if;
        end if;
        
        i := i + 1 ;
      End loop ;
  if(:C_CP_INCONNUS is not null and (LENGTH(:C_CP_INCONNUS)<>0))then
		:C_CP_INCONNUS := :C_CP_INCONNUS || '   Ressource(s) inconnue(s)';
  end if;
  /*
	* Récupération des centres de frais inconnus
	*
	*/
  virgules := ( (LENGTH(:P_PARAM11) - LENGTH(REPLACE(:P_PARAM11,',',NULL)) ) / NVL(LENGTH(','),1) ) ;
  	i:=0;
  	While i < virgules+1
      Loop
      	if (virgules=0) then
      			c_ident := :P_PARAM11;
        elsif (i=0)then
        		pos := INSTR( :P_PARAM11, ',', 1, 1);
        		c_ident := substr( :P_PARAM11, 1, pos-1);
        elsif (i=virgules) then
        		pos := INSTR( :P_PARAM11, ',', 1, i);
        			c_ident := substr( :P_PARAM11, pos+1, LENGTH(:P_PARAM11)-1);
        else
        		pos := INSTR( :P_PARAM11, ',', 1, i);
        		pos1 := INSTR( :P_PARAM11, ',', 1, i+1);
        		c_ident := substr( :P_PARAM11, pos+1, pos1-pos-1);
        end if;
        
        select count(*) into num
        from centre_frais cf
        where to_char(cf.codcfrais) = c_ident;

        if(num=0 or LENGTH(c_ident)>3)then

        	if(:C_CF_INCONNUS='' or :C_CF_INCONNUS is null)then
        		:C_CF_INCONNUS := :C_CF_INCONNUS || c_ident;
        	else
        		:C_CF_INCONNUS := :C_CF_INCONNUS || ', ' || c_ident;
        	end if;
        end if;
        
        i := i + 1 ;
      End loop ;
  if(:C_CF_INCONNUS is not null and (LENGTH(:C_CF_INCONNUS)<>0))then
		:C_CF_INCONNUS := :C_CF_INCONNUS || '   Centre(s) de frais inconnu(s)';
  end if;
  /*
	* Récupération des CA inconnus
	*
	*/
  virgules := ( (LENGTH(:P_PARAM10) - LENGTH(REPLACE(:P_PARAM10,',',NULL)) ) / NVL(LENGTH(','),1) ) ;
  	i:=0;
  	While i < virgules+1
      Loop
      	if (virgules=0) then
      			c_ident := :P_PARAM10;
        elsif (i=0)then
        		pos := INSTR( :P_PARAM10, ',', 1, 1);
        		c_ident := substr( :P_PARAM10, 1, pos-1);
        elsif (i=virgules) then
        		pos := INSTR( :P_PARAM10, ',', 1, i);
        			c_ident := substr( :P_PARAM10, pos+1, LENGTH(:P_PARAM10)-1);
        else
        		pos := INSTR( :P_PARAM10, ',', 1, i);
        		pos1 := INSTR( :P_PARAM10, ',', 1, i+1);
        		c_ident := substr( :P_PARAM10, pos+1, pos1-pos-1);
        end if;
        
        select count(*) into num
				from
					centre_activite ca,
					entite_structure es
				where
					INSTR(c_ident, to_char(es.codcamo,'FM00000')) > 0
					and es.codcamo = ca.codcamo (+);

        if(num=0 or LENGTH(c_ident)>5)then

        	if(:C_CA_INCONNUS='' or :C_CA_INCONNUS is null)then
        		:C_CA_INCONNUS := :C_CA_INCONNUS || c_ident;
        	else
        		:C_CA_INCONNUS := :C_CA_INCONNUS || ', ' || c_ident;
        	end if;
        end if;
        
        i := i + 1 ;
      End loop ;
  if(:C_CA_INCONNUS is not null and (LENGTH(:C_CA_INCONNUS)<>0))then
		:C_CA_INCONNUS := :C_CA_INCONNUS || '   Elément(s) de structure inconnu(s) ou de niveau trop élevé';
  end if;
  /*
	* Récupération des projets inconnus
	*
	*/
  virgules := ( (LENGTH(:P_PROJET) - LENGTH(REPLACE(:P_PROJET,',',NULL)) ) / NVL(LENGTH(','),1) ) ;
  	i:=0;
  	While i < virgules+1
      Loop
      	if (virgules=0) then
      			c_ident := :P_PROJET;
        elsif (i=0)then
