function.textSource:
      function AfterPForm return boolean is
   l_annee0  VARCHAR2(10);
   l_annee1  VARCHAR2(10);
   l_param   VARCHAR2(40);
   l_conso   number(12,2);
   l_listeChefsProjets varchar2(255);
   l_annee_num NUMBER(4);  
   etat_depuis_isac varchar2(4);
 
	 
begin
	

   -- ================================================
   -- Determiner l'annee de traitement N et N+1
   -- ================================================
   
   SELECT to_char(datdebex,'YYYY'),to_char(datdebex,'YYYY') INTO l_annee0,:P_ANNEE
   FROM datdebex;
   
   l_annee1 := to_char(to_number(l_annee0,'FM9999') + 1,'FM9999');
   :p_where := ' and budg.annee(+) = ' || l_annee0 ;
   :p_where := :p_where || ' and budg1.annee(+) = ' || l_annee1 ;
   :p_where := :p_where || ' and conso.annee(+) = ' || l_annee0 ;
   
   --variable servant  indiquer si l'Etat est lanc depuis le menu Mensuelle (Etats PCA4)
   --ou depusi l'cran ISAC
   etat_depuis_isac := NULL ; 
   
		-- ================================================
  	-- cas de l'dition lance dans isac
		-- ================================================
		IF :P_PARAM7 is not null then
			etat_depuis_isac := 'ISAC' ;
				if (:P_PARAM7 !='Tous') then
					:P_WHERE :=  :P_WHERE ||' AND lb.pcpi='||:P_PARAM7;
				else
					l_listeChefsProjets := pack_global.lire_globaldata(:P_GLOBAL).chefprojet;
					:P_WHERE := :P_WHERE ||' AND INSTR('''|| l_listeChefsProjets ||''', TO_CHAR(lb.pcpi, ''FM00000'') ) > 0';
				end if;
		ELSE 
 	  	-- ================================================
	   	-- Soustraire les eventuels $ ou ' ' dans p_param6
	   	-- ================================================
	   	IF NVL(LENGTH(LPAD(:P_PARAM6,7,'0')), 0)!= 0 then 
	   		l_param := rtrim(rtrim(LPAD(:P_PARAM6,7,'0'),'*')) ;
				:p_where := :p_where || ' and  TO_CHAR(lb.codsg, ''FM0000000'') like ''' || l_param ||'%'' ';
	   	END IF;
		END IF;  
		
		   l_annee_num := TO_NUMBER( l_annee0 ) ; 
		   
		   -- Le paramtre 'TRUE' indique que c'est l'Etat pca4 par client sous traitance qui est lanc*
		   -- utilis par la fonction  insert_tmp_coutke_etats
		   
		   :P_NUMSEQ := pack_cout_standard_ke.insert_tmp_coutke_etats(l_annee_num,:P_PARAM6,'TRUE',etat_depuis_isac,:P_PARAM7,:p_param14, :P_GLOBAL);
		   
   --srw.message(1,:P_where);  

  return (TRUE);
end;

      function CF_Annee0 return number is
  l_annee varchar2(4);
begin
  select to_char(datdebex,'YYYY') into l_annee from datdebex;
  return to_number(l_annee,'FM9999');
end;

      function CF_AnN1 return Number is
  l_annee varchar2(4);
begin
  select to_char(datdebex,'YYYY') into l_annee from datdebex;
  return to_number(l_annee,'FM9999') + 1;
end;

      function CF_MoisAnneeFormula return Date is
  l_annee date;
begin
  select moismens into l_annee from datdebex; 
  return l_annee;
end;

      function CF_AnN2Formula return Number is
 l_annee varchar2(4);
