      function M_8FormatTrigger return boolean is
begin
  if  NVL(LENGTH(rtrim(rtrim(LPAD(:P_PARAM6, 7, '0'),'*'))), 0)=3   --dep 16****
  THEN
	return true;
  else 
	return false;
  end if;
end;

      function R_1FormatTrigger return boolean is
begin
  if ( (NVL(:JHCONS, 0)!=0)
	OR (NVL(:JHREEST, 0)!=0) 
	OR (NVL(:PROPOJH, 0)!=0)
	OR (NVL(:NOTIFJH, 0)!=0)
	OR (NVL(:PROPOMOJH, 0)!=0)
	OR (NVL(:ARBITJH, 0)!=0)
	OR (NVL(:PROPOJH1, 0)!=0)
	OR (NVL(:PROPOMOJH1, 0)!=0) )
  then
	return (TRUE);
  else
	return (FALSE);
  end if;
end;

      function R_type2FormatTrigger return boolean is
begin
  if :PTYPEB!='1 ' then
	return (FALSE);
  else
 	 return (TRUE);
  end if;
end;

      function B_27FormatTrigger return boolean is
begin
  if :TYPE2b='T1' then
  	return (TRUE);
  else
  	return (FALSE);
  end if;
end;

      function R_3FormatTrigger return boolean is
begin
  if :pid = :propid then 
  return (FALSE);
  elsif :propid is null then
	return (FALSE);
else
return (TRUE);
  end if;
end;

      function CF_TYPPROJFormula return Char is
begin
   if to_number(:TYPPROJ)<7 then
			return 'Type 1-6 ou 8';
   elsif to_number(:TYPPROJ)=7 then
			return 'Type 7';
   elsif to_number(:TYPPROJ)=8 then
			return 'Type 1-6 ou 8';
   elsif to_number(:TYPPROJ)=9 then
			return 'Type 9';
	 else
			return 'Type ?';
   end if;
end;

