function.textSource:
      function AfterPForm return boolean is

	l_param VARCHAR2(10);
	l_perim_me	VARCHAR2(1000) := '';
	
begin
  
	:p_where :='';
	:p_order := '';

	-- Ajout du perim ME
	l_perim_me := pack_global.lire_perime(:P_GLOBAL);		
	:p_where := :p_where || ' AND sit.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 


	IF NVL(LENGTH(LPAD(:P_PARAM6, 7, '0')), 0) != 0 then 
			-- ajout de '0' a gauche sur 7 caracteres + suppression des '*'
	  l_param := rtrim(rtrim(LPAD(:P_PARAM6, 7, '0'),'*')) ;
		:p_where := :p_where || ' and  TO_CHAR(sit.codsg, ''FM0000000'') like ''' || l_param || '%'' ';
	END IF;
   

	if('C'=:P_PARAM10) then
		:p_order := 'order by tri_chrono,nom';
	else
		:p_order := 'order by nom';
	end if;

	
 
  --srw.message(1,:P_where);  
  return (true);
  end;

      function M_GROUPEFormatTrigger return boolean is
-- CAS : pas  d etoile dans le dpg ou param7  3
-- se rapporte  la requete Q_GROUPE
begin
     
   if INSTR(:P_Param6,'*',1)=0  then
   	   	return (TRUE);
   elsif :P_Param7=3 or :P_Param7 is null then
    	return (TRUE);
   else
    	return (FALSE);
   end if;
end;

      function M_DEPFormatTrigger return boolean is
-- CAS 1 : se rapporte  la requete Q_DEP
-- si on coche par departement cad P_PARAM7=1
-- si on coche par departement cad P_PARAM7=1 mais que l'on entre un Dep/Pole/** : par pole
begin
	if INSTR(:P_Param6,'*',1)=0  then
    	return (FALSE);
    		elsif (:P_Param7=1 AND substr(LPAD(:P_PARAM6, 7, '0'),4,4)='****')   then
     			return (TRUE);
   		else
     			return (FALSE);
   	end if;
end;

      function M_POLEFormatTrigger return boolean is
begin
	
	if INSTR(:P_Param6,'*',1)=0  then
    	return (FALSE);
   		elsif (:P_Param7=2) OR (:P_Param7=1 AND INSTR(:P_Param6,'*',1)=6)  then
    	    			return (TRUE);
   		else
   			return (FALSE);
   end if;
end;

      function F_fincontratFormatTrigger return boolean is
l_date varchar2(10);
begin
l_date := substr(:fincontrat,7,4) || substr(:fincontrat,3,4) || substr(:fincontrat,1,2);


if l_date <= to_char(sysdate,'yyyy/mm/dd') then

-- pour les contrats finissant avant la date de l edition
-- on remplace la valeur par (vide)
SRW.SET_FIELD_CHAR(0,' ');
return (TRUE);
else
  return (TRUE);
end if;
end;

