      function R_G_groupe1FormatTrigger return boolean is
reste number;
begin
reste := mod(:CS_1,2);
if reste=0 then
-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='white';
 srw.set_attr (0,srw.attr);
end if;
  return (TRUE);
end;

