function.textSource:
      function AfterPForm return boolean is
l_codsg VARCHAR2(7);
begin
  
:p_where :='';
:p_where1 :='';
:p_where2 :='';
:p_order := '';
:p_order_g := '';

	if LPAD(:P_PARAM6, 7, '*')='*******' then
			l_codsg := '';
	else
		if substr(LPAD(:P_PARAM6, 7, '0'),4,4)='****' then
			l_codsg := substr(LPAD(:P_PARAM6, 7, '0'),1,3);
		else
			if substr(LPAD(:P_PARAM6, 7, '0'),6,2)='**' then
					l_codsg := substr(LPAD(:P_PARAM6, 7, '0'),1,5);
			else
					l_codsg := LPAD(:P_PARAM6, 7, '0');
		  end if;
		end if;
	end if;		
		:p_where := 'and  to_char(sit.codsg,''FM0000000'') like ''' || l_codsg ||'%'' ';
		:p_where1 := 'and  to_char(cont.codsg,''FM0000000'') like ''' || l_codsg ||'%'' ';
		:p_where2 := 'and  to_char(pro.divsecgrou,''FM0000000'') like ''' || l_codsg ||'%'' ';

-- classement alphabetique ou chronologique
IF :P_PARAM10 = 'A' OR :P_PARAM10 is null THEN :p_order := 'order by 3,4';
:p_order_g := 'order by nom_g';
 ELSIF :P_PARAM10 = 'C' THEN :p_order := 'order by 11,3';
:p_order_g := 'order by fin_g,nom_g';

END IF;
		
	
 
  --srw.message(1,:P_where);  
  return (true);
  end;

      function M_persgFormatTrigger return boolean is
-- CAS 2 : etoile dans le dpg ou param7 vide
-- se rapporte à la requete Q_LISTPEREXT_G
begin

  	return (TRUE);
end;

      function M_liste_nom2FormatTrigger return boolean is
reste number;
begin
reste := mod(:CS_NUM_G,2);
if reste=0 then
-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='white';
 srw.set_attr (0,srw.attr);
end if;
  return (TRUE);
end;

      function F_coutnego2FormatTrigger return boolean is
  l_date varchar2(10);
l_cont varchar2(10);
begin
l_date := substr(:fincontrat_g,7,4) || substr(:fincontrat_g,3,4) || substr(:fincontrat_g,1,2);
l_cont := substr(:contrat_futur_g,7,4) || substr(:contrat_futur_g,3,4) || substr(:contrat_futur_g,1,2);

if l_cont > l_date then
return (TRUE);
else
  return (FALSE);
end if;
end;

      function F_fincontratnego2FormatTrigger return boolean is
  l_date varchar2(10);
l_cont varchar2(10);
begin
l_date := substr(:fincontrat_g,7,4) || substr(:fincontrat_g,3,4) || substr(:fincontrat_g,1,2);
l_cont := substr(:contrat_futur_g,7,4) || substr(:contrat_futur_g,3,4) || substr(:contrat_futur_g,1,2);

if l_cont > l_date then
return (TRUE);
else
  return (FALSE);
end if;
end;

      function F_fincontrat2FormatTrigger return boolean is
l_date varchar2(10);
begin
l_date := substr(:fincontrat_g,7,4) || substr(:fincontrat_g,3,4) || substr(:fincontrat_g,1,2);

if l_date <= to_char(sysdate,'yyyy/mm/dd') then

-- pour les contrats finissant avant la date de l edition
-- on remplace la valeur par (vide)
SRW.SET_FIELD_CHAR(0,' ');
return (TRUE);
else
  return (TRUE);
end if;
end;

      function R_5FormatTrigger return boolean is
