function.textSource:
      function CF_TOT_SG_GRATUITFormula return Number is
begin
  RETURN :CS_TOTAL_SG_GRA + :CS_TOTAL_SG_INT + :CS_TOTAL_SG_STA + :CS_TOT_IFO;
end;

      function CF_TOTAL_SSIIFormula return Number is
l_total number;
begin
l_total := :CS_SSII - (:CS_TOT_GRATUIT - :CF_TOT_SG_GRATUIT);
  
  return l_total;

end;

      function CF_TOTAL_SGFormula return Number is
l_total number;
begin
  l_total := :CS_SG - :CF_TOT_SG_GRATUIT;
  return l_total;
end;

      function CF_TOTAL_RESSFormula return Number is
l_total number;
begin
  l_total := :CF_TOTAL_SG + :CF_TOTAL_SSII + :CS_TOT_GRATUIT;
  
  return l_total;
end;

      function CF_TOT_SG_GRATUIT_gFormula return Number is
begin
  RETURN :CS_TOTAL_SG_GRA_g + :CS_TOTAL_SG_INT_g + :CS_TOTAL_SG_STA_g + :CS_TOT_IFO_g;
end;

