begin
  l_total := :CS_SG_g - :CF_TOT_SG_GRATUIT_g;
  return l_total;
end;

      function CF_TOTAL_SSII_gFormula return Number is
l_total number;
begin
  l_total := :CS_SSII_g - (:CS_TOT_GRATUIT_g - :CF_TOT_SG_GRATUIT_g);
  
