      function M_10FormatTrigger return boolean is
-- CAS : pas  d etoile dans le dpg ou param7 à 3
-- se rapporte à la requete Q_GROUPE
begin
     
   if INSTR(:P_Param6,'*',1)=0  then
    	return (TRUE);
   elsif :P_Param7=3 or :P_Param7 is null then
    	return (TRUE);
   else
    	return (FALSE);
   end if;
end;

      function M_9FormatTrigger return boolean is
-- CAS 2 : se rapporte à la requete Q_POLE
-- si on coche par pole cad P_PARAM7=2
-- si on coche par departement cad P_PARAM7=1 mais que l'on entre un Dep/Pole **
begin
	
	if INSTR(:P_Param6,'*',1)=0  then
    	return (FALSE);
   		elsif (:P_Param7=2) OR (:P_Param7=1 AND INSTR(:P_Param6,'*',1)=6)  then
    			return (TRUE);
   		else
   			return (FALSE);
   end if;
end;

      function AfterPForm return boolean is
	l_param VARCHAR2(10);
	l_perim_me	VARCHAR2(1000) := '';

begin

	-- Ajout du perim ME
	l_perim_me := pack_global.lire_perime(:P_GLOBAL);	
		
	:p_where := ' AND sit.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 
  :p_where1 := ' AND cont.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 
  :p_where2 := ' AND pro.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 
  
	IF NVL(LENGTH(LPAD(:P_PARAM6, 7, '0')), 0) != 0 then 
			-- ajout de '0' a gauche sur 7 caracteres + suppression des '*'
	  l_param := rtrim(rtrim(LPAD(:P_PARAM6, 7, '0'),'*')) ;
		:p_where := :p_where || ' and  TO_CHAR(sit.codsg, ''FM0000000'') like ''' || l_param || '%'' ';
		:p_where1 := :p_where1 || ' and  TO_CHAR(cont.codsg, ''FM0000000'') like ''' || l_param || '%'' ';
		:p_where2 := :p_where2 || ' and  TO_CHAR(pro.codsg, ''FM0000000'') like ''' || l_param || '%'' ';
				
	END IF;
 
  --srw.message(1,:P_where);  
  return (true);

end;

      function R_liste_nomFormatTrigger return boolean is
reste number;
begin
reste := mod(:CS_NUM,2);
if reste=0 then
-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='white';
 srw.set_attr (0,srw.attr);
end if;
  return (TRUE);
end;

      function R_liste_nom_gFormatTrigger return boolean is
reste number;
begin
reste := mod(:CS_NUM_g,2);
if reste=0 then
-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='white';
 srw.set_attr (0,srw.attr);
end if;
  return (TRUE);
end;

