function.textSource:
      function AfterPForm return boolean is
	l_nom_etat VARCHAR2(10);
	l_mois_annee_traitee VARCHAR2(10);
	--l_perimo VARCHAR2(500);
	l_perim_me	VARCHAR2(1000) := '';
	
begin
/*****************************************************************
--------------------------------------
RAPPEL DES PARAMETRES POUR PRODEC2.RDF
--------------------------------------
P_PARAM6 = Nom état : PRODEC2, PRODEC3, PRODEC4 ou PRODECL
	            (Si vide ou autre, on prend PRODEC2)
P_PARAM4 = Code direction (pour PRODEC2 et PRODEC4)
P_PARAM7 = Mois de traitement au format MM/AAAA (pour PRODEC3 et PRODECL)
P_PARAM8 = Code DPG (pour PRODEC3 et PRODECL)
P_PARAM9 = Code ligne BIP (pour PRODECL) 
P_PARAM10 = Code référentiel (pour PRODEC4 )
	    - PXXXX pour un code Projet
	    - AXXXX pour un code Application

******************************************************************/


/****************************************************************
* DEBUT DU BOUT DE CODE QUI N'EXISTE PAS DANS AfterPForm de PRODEC2.RDF
* Après ce bout de code, tout le reste du Report est identique à PRODEC2.RDF !!!
****************************************************************/
--------------------------------------------------------------------------
-- POUR PRODEC3_MENS.RDF (PRODEC3 appelé à partir du menu MENSUELLE) 
-- JS ENVOI L'UNIQUE PARAMETRE Code DP dans P_PARAM6.
-- IL FAUT POSITIONNER LES PARAMETRES EN CONSEQUENCE :
--
-- P_PARAM7 (Mois de traitement) <-- Date mensuelle de datdebex 
-- P_PARAM8 (Code DPG)           <-- P_PARAM6 fourni par JS + ** (i.e Code DP + **)
-- P_PARAM6 (Nom état)           <-- PRODEC3 (Seule différence avec PRODECL_MENS.RDF!!!)
--------------------------------------------------------------------------

   SELECT TO_CHAR(moismens, 'MM/YYYY') INTO l_mois_annee_traitee  FROM datdebex;
 --  if ( SUBSTR(l_mois_annee_traitee,1,2)='01' )
 --  then l_mois_annee_traitee:= TO_CHAR(TRUNC(TRUNC (to_date(l_mois_annee_traitee,'MM/YYYY'),'MONTH')-1,'MONTH'),'MM/YYYY');
 --  end if;

   :P_PARAM7 := l_mois_annee_traitee;	
   :P_PARAM8 := LPAD(:P_PARAM6, 7, '0');
   :P_PARAM6 := 'PRODEC3';
   :P_PARAM9 := NULL;
   :P_LIBELLE_ETAT := 'Mensuelle : Etat détaillé client par client' ;
   
   --srw.message(1, 'P_Param7 = ' ||  :P_PARAM7);
   --srw.message(1, 'P_Param6 = ' ||  :P_PARAM6);

/****************************************************************
* FIN DU BOUT DE CODE QUI N'EXISTE PAS DANS AfterPForm PRODEC2.RDF
****************************************************************/


  l_nom_etat := UPPER(LTRIM(RTRIM(:P_PARAM6)));
  IF (l_nom_etat IS NULL) THEN 
	l_nom_etat := 'PRODEC2';
	:P_PARAM6  := 'PRODEC2'; /* faut absolument Positionner P_PARAM6 !!! */
  END IF;

  IF ( l_nom_etat = 'PRODEC2'  ) THEN
	:P_ORDER_BY := ' ORDER BY  lbi.pid '; /* Tri par Code ligne BIP */
  ELSIF (l_nom_etat = 'PRODEC4') THEN
	:P_ORDER_BY := ' ORDER BY  lbi.pid '; /* Tri par Code ligne BIP */
  ELSIF (l_nom_etat = 'PRODEC3') THEN
		:P_ORDER_BY := ' ORDER BY  lbi.clicode, lbi.pid ';  /* Tri par Code MO et par Code ligne BIP */
  ELSIF (l_nom_etat = 'PRODECL') THEN
	:P_ORDER_BY := ' ORDER BY  lbi.codsg, lbi.pid ';
  ELSE
	--srw.message(1, 'Etat inconnu pour ce traitement  (ON NE FAIT RIEN!!!) : ' || l_nom_etat );
	RETURN(FALSE);
  END IF;

  --srw.message(1, '[P_ORDER_BY= ' || :P_ORDER_BY || '] ' ); 
 
  --l_perimo := pack_global.lire_perimo(:p_global);
	l_perim_me := pack_global.lire_perime(:P_GLOBAL);		

	-- QC 1281
		:P_WHERE :=' AND lbi.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 ) '; 

	  		
	--	if length(l_perimo) != 0 AND '000000000' not in (l_perimo) AND '099988888' not in (l_perimo) then	  	
	--			:P_WHERE := :P_WHERE || ' and ( lbi.clicode in (select clicode from vue_clicode_perimo where BDCLICODE in ('||l_perimo||') ) 
	--															or lbi.clicode_oper in (select clicode from vue_clicode_perimo where BDCLICODE in ('||l_perimo||') ) )';
	--	end if;
		
  return (TRUE);
end;

      function R_consoFormatTrigger return boolean is
begin
  IF :dc_id is NULL then
	return (FALSE);
  END IF;
  
  IF :dc_typeb != 'A' then
	return(FALSE);
  end if;

  return (TRUE);
end;

