	return(FALSE);
  end if;

  return (TRUE);
end;

      function R_pid_consoFormatTrigger return boolean is
begin
  IF :dc_typeb != 'A' then
	return(FALSE);
  end if;
  return (TRUE);
end;

      function BeforeReport return boolean is
	l_heure_deb VARCHAR2(30);
	l_heure_fin VARCHAR2(30);

	l_nom_etat VARCHAR2(10);
	l_code_direction VARCHAR2(10);
	l_mois_traitement VARCHAR2(10);
	l_code_dpg VARCHAR2(10);
	l_code_ligne_bip VARCHAR2(10);
		l_code_appli VARCHAR2(10);
	l_code_projet VARCHAR2(10);

	l_annee_exercice VARCHAR2(4); /* Année Exercice */
 
	l_date_traite DATE;  
	/*********************************************************************
	* l_date_traite : Date à prendre en compte pour le traitement 
	* 	Pour PRODEC2 et PRODEC4 : 01/12/Année Exercice (lue dans datdebex)
	* 	Pour PRODEC3 et PRODECL : 01/Mois Saisi/Année Saisie
	**********************************************************************/	
	
begin

/*****************************************************************
P_PARAM6 = Nom état : PRODEC2, PRODEC3, PRODEC4 ou PRODECL
	            (Si vide ou autre, on prend PRODEC2)
P_PARAM4 = Code direction (pour PRODEC2 et PRODEC4)
P_PARAM7 = Mois de traitement au format MM/AAAA (pour PRODEC3 et PRODECL)
P_PARAM8 = Code DPG (pour PRODEC3 et PRODECL)
P_PARAM9 = Code ligne BIP (pour PRODECL) 
P_PARAM10 = code appli (pour PRODEC4 )
P_PARAM12 = code projet (pour PRODEC4 )
******************************************************************/
  l_nom_etat := UPPER(LTRIM(RTRIM(:P_PARAM6)));
  IF (l_nom_etat IS NULL) THEN 
	l_nom_etat := 'PRODEC2';
  END IF;

  IF ( l_nom_etat = 'PRODEC2'  ) THEN
	l_code_direction   := LTRIM(RTRIM(:P_PARAM4));
	l_code_dpg         := NULL;
	l_code_ligne_bip   := NULL;
	l_code_appli :=  NULL;
	l_code_projet :=  NULL;
	SELECT TO_CHAR(datdebex, 'YYYY') INTO l_annee_exercice FROM datdebex;
	l_date_traite := TO_DATE('01/12/' || l_annee_exercice, 'DD/MM/YYYY');

	-- Positionnement P_PARAM7 par souci homogeneité avec PRODEC3 et PRODECL 
	-- Puisqu'on traite tous les mois ici, On fait comme Si on avait saisi 12/<Année Exercice>!!!
	:P_PARAM7 := '12/' || l_annee_exercice; 
	:P_PARAM6 := l_nom_etat;

  ELSIF (l_nom_etat = 'PRODEC4') THEN
	l_code_direction   := LTRIM(RTRIM(:P_PARAM4));
	l_code_dpg := NULL;
	l_code_ligne_bip := NULL;
	l_code_appli :=  LTRIM(RTRIM(:P_PARAM10));
	l_code_projet :=  LTRIM(RTRIM(:P_PARAM12));
	SELECT TO_CHAR(datdebex, 'YYYY') INTO l_annee_exercice FROM datdebex;
	l_date_traite := TO_DATE('01/12/' || l_annee_exercice, 'DD/MM/YYYY');

	-- Positionnement P_PARAM7 par souci homogeneité avec PRODEC3 et PRODECL 
	-- Puisqu'on traite tous les mois ici, On fait comme Si on avait saisi 12/<Année Exercice>!!!
	:P_PARAM7 := '12/' || l_annee_exercice;
	:P_PARAM6 := l_nom_etat;

  ELSIF (l_nom_etat = 'PRODEC3') THEN
	l_code_direction   := NULL;
	l_code_dpg         := LTRIM(RTRIM(:P_PARAM8));
	l_code_ligne_bip   := NULL;
	l_code_appli :=  NULL;
	l_code_projet :=  NULL;
	l_date_traite := TO_DATE('01/' || LTRIM(RTRIM(:P_PARAM7)), 'DD/MM/YYYY');
	:P_PARAM6 := l_nom_etat;

  ELSIF (l_nom_etat = 'PRODECL') THEN
	l_code_direction   := NULL;
	l_code_dpg         := LTRIM(RTRIM(:P_PARAM8));
	l_code_ligne_bip   := LTRIM(RTRIM(:P_PARAM9));
	l_code_appli :=  NULL;
	l_code_projet :=  NULL;
	l_date_traite := TO_DATE('01/' || LTRIM(RTRIM(:P_PARAM7)), 'DD/MM/YYYY');
	:P_PARAM6 := l_nom_etat;

  ELSE
	--srw.message(1, 'Etat inconnu pour ce traitement  (ON NE FAIT RIEN!!!) : ' || l_nom_etat );
	RETURN(FALSE);
  END IF;
       
	  
  --srw.message(1, '[Nom Etat=' || NVL(l_nom_etat, 'NULL!!!') || '] ' ); 
  --srw.message(1, '[Code Direction=' || NVL(l_code_direction, 'NULL!!!') || '] ' ); 
  --srw.message(1, '[Code DPG=' || NVL(l_code_dpg, 'NULL!!!') || '] ' ); 
  --srw.message(1, '[Code ligne BIP=' || NVL(l_code_ligne_bip, 'NULL!!!') || '] ' ); 
  --srw.message(1, '[Code Appli =' || NVL(l_code_appli, 'NULL!!!') || '] ' ); 
  --srw.message(1, '[Code Projet =' || NVL(l_code_projet, 'NULL!!!') || '] ' ); 
  --srw.message(1, '[l_date_traite=' || TO_CHAR(l_date_traite, 'DD/MM/YYYY') || '] ' );

  l_heure_deb := TO_CHAR(SYSDATE, 'DD/MM/YYYY :HH24:MI:SS');
  :P_NUMSEQ_1 := pack_prodec2.tmp_sstache2(l_nom_etat, l_code_direction, l_code_dpg, l_code_ligne_bip, l_code_appli,l_code_projet,  l_date_traite);
  l_heure_fin := TO_CHAR(SYSDATE, 'DD/MM/YYYY :HH24:MI:SS');			     
  
  --srw.message(1, 
--'[P_NUMSEQ_1 = ' || TO_CHAR(:p_numseq_1) || '] ' || '[Deb = ' || l_heure_deb || '] ' || '[Fin = ' || l_heure_fin || '] ');

 IF  (:P_NUMSEQ_1 = 0)   THEN
	--srw.message(1, ' P_NUMSEQ_1 = 0  : ARRET!!!');
	RETURN (FALSE);
  END IF;


  l_heure_deb := TO_CHAR(SYSDATE, 'DD/MM/YYYY :HH24:MI:SS');
  :P_NUMSEQ_2 := pack_prodec2.tmp_sstache_budget2(l_nom_etat, l_code_direction, l_code_dpg, l_code_ligne_bip, l_code_appli,l_code_projet,  l_date_traite);
  l_heure_fin := TO_CHAR(SYSDATE, 'DD/MM/YYYY :HH24:MI:SS');			

  --srw.message(3, 
--'[P_NUMSEQ_2 = ' || TO_CHAR(:p_numseq_2) || '] ' || '[Deb = ' || l_heure_deb || '] ' || '[Fin = ' || l_heure_fin || '] ');

 IF  (:P_NUMSEQ_2 = 0)   THEN
	--srw.message(1, ' P_NUMSEQ_2 = 0  : ARRET!!!');
	RETURN (FALSE);
  END IF;

  return (TRUE);


end;

      function AfterReport return boolean is
begin


 -- supprime les lignes de l'edition dans tmp_conso_sstache
  delete from tmp_conso_sstache
  where  numseq = :p_numseq_1;

 -- supprime les lignes de l'edition dans tmp_budget_sstache
  delete from tmp_budget_sstache
  where  numseq = :p_numseq_2;

  return (true);   -- OK

   exception
      when others then
         --srw.message(1, ' AfterReport : Erreur  vidage tables temporaires !!! ');
         return(false); -- NOK
 

 
  return (TRUE);
end;

      function CF_AN_M1Formula return Number is
begin
  -- Année N-1
  RETURN( TO_NUMBER(TO_CHAR(:dc_date_traite, 'YYYY')) - 1 );
end;

      function CF_ANFormula return Number is
begin
    -- Année N
  RETURN( TO_NUMBER(TO_CHAR(:dc_date_traite, 'YYYY')) );
end;

      function CF_AN_P1Formula return Number is
begin
    -- Année N+1
  RETURN( TO_NUMBER(TO_CHAR(:dc_date_traite, 'YYYY')) + 1 );
end;

      function CF_AN_P2Formula return Number is
begin
    -- Année N+2
  RETURN( TO_NUMBER(TO_CHAR(:dc_date_traite, 'YYYY')) + 2 );
end;

      function CF_AN_P3Formula return Number is
begin
    -- Année N+3
  RETURN( TO_NUMBER(TO_CHAR(:dc_date_traite, 'YYYY')) + 3 );
end;

