      function CF_LIB_TYPE_SSTACHEFormula return VARCHAR2 is
	l_res VARCHAR2(50) := 'Sous-Tâches Sous-Traitance';
begin
  	IF ( :dc_flag_type_sstache = '1' )
	THEN
		l_res := 'Sous-Tâches Projet';
	END IF;

	RETURN (l_res);
end;

      function CF_LIB_TOTYP_SSTFormula return VARCHAR2 is
	l_res VARCHAR2(50) := 'Total de la sous-traitance';
begin
  	IF ( :dc_flag_type_sstache = '1' )
	THEN
		l_res := 'Total projet';
	END IF;

	RETURN (l_res);
end;

      function M_TOT_TYPE_SSTACHEFormatTrigge return boolean is
begin

  --IF ( (:dc_flag_type_sstache = '1') OR (:dc_typeb != 'A') ) THEN
