      function F_GROUPEFormatTrigger return boolean is
Text varchar2(100);
begin
  IF substr(LPAD(:P_PARAM8, 7, '0'),6,2)='**' THEN
	Text := ' ';
  ELSE
	Text := '/Groupe ';
  END IF;  
  SRW.SET_FIELD_CHAR(0,Text); 
	
  return (TRUE);
end;
