function.textSource:
      function R_CP_PID2FormatTrigger return boolean is
reste number;
begin
	
	reste := mod(:CS_NUM_CP_PID,2);
	--reste:=1;
	if reste=1 then
		-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
		srw.attr.bfcolor :='white';
		srw.set_attr (0,srw.attr);
	end if;
  
  return (TRUE);
end;

      function R_CP_RESS1FormatTrigger return boolean is
reste number;
begin
  reste := mod(:CS_NUM_RESS,2);
	--reste:=1;
	if reste=1 then
		-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
		srw.attr.bfcolor :='white';
		srw.set_attr (0,srw.attr);
	end if;
  return (TRUE);
end;

      function AfterPForm return boolean is

	liste_chefs_projet 	varchar2(1024);
	liste_chefs  				varchar2(1024);
	virgules 						number;
	chef 								varchar(5);
	i 									number;
	pos 								number;
	pos1 								number;
	user_rtfe 					varchar(60);
	t_ident_utilisateur number;

	
	CURSOR C IS select DISTINCT replace(rt.chef_projet,';',',') from RTFE rt where rt.ident = :P_PARAM6;
	CURSOR c_utilisateur IS
					SELECT IDENT FROM BIP.RTFE_USER r WHERE UPPER(USER_RTFE) = UPPER(:P_USER_RTFE);
					
BEGIN
	:P_WHERE := '';
	
	
	
  SELECT DATDEBEX INTO :P_DATDEBEX
  FROM DATDEBEX ;
  
  :P_USER_RTFE :=pack_global.lire_globaldata(:P_GLOBAL).idarpege;

	OPEN c_utilisateur;
	FETCH c_utilisateur INTO t_ident_utilisateur;
	IF c_utilisateur%NOTFOUND THEN
		t_ident_utilisateur := -1;
	END IF; 
	CLOSE c_utilisateur;
	
	-- Valeur de la veille au soir
  IF ( NVL(t_ident_utilisateur,-1) <> :P_PARAM6 ) THEN
  	
  	:P_DATE_EXTRACTION := 'LA VEILLE AU SOIR';
  	:P_USER_TIER := 1;
	 	OPEN C;
	 	LOOP
	 	FETCH C INTO liste_chefs;
	 	EXIT WHEN C%NOTFOUND;
	 	
	 		IF liste_chefs_projet IS NULL THEN
	 			liste_chefs_projet := liste_chefs;
	 		ELSE
	 			liste_chefs_projet := liste_chefs_projet || ',' || liste_chefs;
	 		END IF;
 	
	 	END LOOP;
	  CLOSE C;
  
	-- Valeur du jour ( celui de l utilisateur connecte )
	ELSE

	  	 SELECT TO_CHAR(SYSDATE,'DD/MM/YYYY HH24:MI:SS') INTO :P_DATE_EXTRACTION FROM DUAL;
	  	:P_USER_TIER := 0;
	  	liste_chefs_projet := REPLACE(pack_global.lire_globaldata(:P_GLOBAL).chefprojet,';',',') ;
	  	  	
  END IF; 
  
  
	if(liste_chefs_projet is not null) then
	  virgules := ( (LENGTH(liste_chefs_projet) - LENGTH(REPLACE(liste_chefs_projet,',',NULL)) ) / NVL(LENGTH(','),1) ) ;
	  	i:=0;
	  	While i < virgules+1
	      Loop
	      	if (virgules=0) then
	      			chef := lpad(liste_chefs_projet,5,'0');
	        elsif (i=0)then
	        		pos := INSTR( liste_chefs_projet, ',', 1, 1);
	        		chef := lpad(substr( liste_chefs_projet, 1, pos-1),5,'0');
	        elsif (i=virgules) then
	        		pos := INSTR( liste_chefs_projet, ',', 1, i);
	        		chef := 	lpad(substr( 	liste_chefs_projet, pos+1, LENGTH(liste_chefs_projet)-1),5,'0');
	        else
	        		pos := INSTR( liste_chefs_projet, ',', 1, i);
	        		pos1 := INSTR( liste_chefs_projet, ',', 1, i+1);
	        		chef := lpad(substr( liste_chefs_projet, pos+1, pos1-pos-1),5,'0');
	        end if;
	        if :P_CHEF_PROJET is null then
	        	:P_CHEF_PROJET := chef;
	        else
	        	:P_CHEF_PROJET := :P_CHEF_PROJET || ',' || chef;
	        end if;
	        i := i + 1 ;
	      End loop ;
  end if;  
  
  
	:P_WHERE := ' AND (  ';
	:P_WHERE := :P_WHERE || ' INSTR (''' || :P_CHEF_PROJET || ''', TO_CHAR(situ.cpident,''FM00000'') ) > 0 ';
	:P_WHERE := :P_WHERE || ' OR INSTR (''' || :P_CHEF_PROJET || ''', TO_CHAR(situ.ident,''FM00000'') ) > 0 ';
	:P_WHERE := :P_WHERE || '    )';
      
  return (TRUE);
end;

      function CS_DFormula return Char is
begin
  if(INSTR(:P_CHEF_PROJET,to_char(:ress_code,'FM00000'))>0)then
  	return ('D');
  else
  	return ('');
  end if;
end;

