      function M_3FormatTrigger return boolean is
begin
	
  IF :P_USER_TIER = 0 THEN
  	return (TRUE);
  ELSE
  	RETURN (FALSE);
  END IF;
  
end;

      function M_1FormatTrigger return boolean is
begin
  
  IF :P_USER_TIER = 1 THEN
  	return (TRUE);
  ELSE
  	RETURN (FALSE);
  END IF;
  
end;
