      ------------------------------------------------------------------------------------------
-- Rôle : Construire un Where conditionnel. La chaine construite sera affectée au parametre
-- de P_WHERE. 
------------------------------------------------------------------------------------------
function AfterPForm return boolean is
	
	l_param varchar2(10);
	l_perim_me	VARCHAR2(1000) := '';
	
begin
	
  :p_where := '';

	-- Filtre du perim ME
	l_perim_me := pack_global.lire_perime(:P_GLOBAL);		
	:P_WHERE :=' AND lb.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 


	IF :P_PARAM6 IS NOT NULL THEN
		-- ajout de '0' a gauche sur 7 caracteres + suppression des '*'
	  l_param := rtrim(rtrim(LPAD(:P_PARAM6, 7, '0'),'*')) ;
		:p_where := :p_where || ' and  TO_CHAR(lb.codsg, ''FM0000000'') like ''' || l_param ||'%'' ';
		
	END IF;
	
  -- --srw.message(1,' ' || :p_where);
  RETURN (TRUE);
END;

      FUNCTION RtrimEtoile(P_Text In VARCHAR2) RETURN VARCHAR2 IS
  meta VARCHAR2(1);
  L_Text VARCHAR2(7);
BEGIN
  L_Text := P_Text;
  meta := SUBSTR(P_Text, NVL(length(P_Text), 0),1);
  IF (meta = ' ') OR (meta = '*') THEN
      L_Text := RTRIM(P_Text,meta);   
  END IF;
  RETURN(L_Text);
END;
