function.textSource:
      function AfterPForm return boolean is
   l_annee0  VARCHAR2(10);
   l_annee1  VARCHAR2(10);
   l_annee2  VARCHAR2(10);
   l_param   VARCHAR2(40);
	 l_conso number(12,2);
	 l_perim_me	VARCHAR2(1000) := '';
	 
begin
   -- ================================================
   -- Determiner l'annee de traitement N et N+1
   -- ================================================
   SELECT to_char(MOISMENS,'YYYY') INTO l_annee0 FROM DATDEBEX; 
   --l_annee0 := to_char(add_months(sysdate,-1),'YYYY');
   l_annee1 := to_char(to_number(l_annee0,'FM9999') + 1,'FM9999');
   l_annee2 := to_char(to_number(l_annee0,'FM9999') + 2,'FM9999');
   :p_where := ' and budg.annee(+) = ' || l_annee0 ;
   :p_where := :p_where || ' and budg1.annee(+) = ' || l_annee1 ;
   :p_where := :p_where || ' and budg2.annee(+) = ' || l_annee2 ;
   
	-- Filtre du perim ME
	l_perim_me := pack_global.lire_perime(:P_GLOBAL);		
	:p_where := :p_where || ' AND lb.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 
   
  -- Recupere que les codsg ouvert
  :p_where := :p_where || 'AND s.TOPFER = ''O'' ';
  
   -- ================================================
   -- Soustraire les eventuels $ ou ' ' dans p_param6
   -- ================================================
   IF NVL(LENGTH(:P_PARAM6), 0)!= 0 then 
      l_param := rtrim(rtrim(LPAD(:p_param6, 7, '0'),'*')) ;
			:p_where := :p_where || ' and  TO_CHAR(lb.codsg, ''FM0000000'') like ''' || l_param ||'%'' ';
   END IF;
   --srw.message(1,:P_where);  

  return (TRUE);
end;

      function CF_Annee0 return number is
  l_annee varchar2(4);
begin
  select to_char(MOISMENS,'YYYY') into l_annee from DATDEBEX;
  return to_number(l_annee,'FM9999');
end;

      function CF_MoisAnneeFormula return Date is
  l_annee date;
begin
  select MOISMENS into l_annee from DATDEBEX;
  return l_annee;
end;

      function CF_RAFFormula return Number is
begin
	if :REESTJH is null and :CF_ConsoAn is null then
		return :REESTJH;
	else
		return nvl(:REESTJH,0)-nvl(:CF_ConsoAn,0);
	end if;
end;

      function CF_ConsoMensFormula return Number is
begin
	if :ConsoMensSG is null and :ConsoMensSSII is null then
		return :ConsoMensSG;
	else
		return nvl(:ConsoMensSG,0)+nvl(:ConsoMensSSII,0);
	end if;
end;

      function CF_G4_RAFFormula return Number is
begin
	if :CS_G4_reestjh is null and :CS_G4_consojh is null then
		return :CS_G4_reestjh;
	else
		return nvl(:CS_G4_reestjh,0)-nvl(:CS_G4_consojh,0);
	end if;
end;

      function CF_G2_RAFFormula return Number is
begin
