	else
		return nvl(:CS_G2_reestjh,0)-nvl(:CS_G2_consojh,0);
	end if;
end;

      function CF_Total_RAFFormula return Number is
begin
	if :CS_Total_reestjh is null and :CS_Total_Consojh is null then
		return :CS_Total_reestjh;
	else
		return nvl(:CS_Total_reestjh,0)-nvl(:CS_Total_Consojh,0);
