end;

      function CF_ConsoAnFormula return Number is
begin
	if :ConsoAnSSII IS NULL and :ConsoAnSG IS NULL then
		return :ConsoAnSSII;
	else
		return nvl(:ConsoAnSSII,0)+nvl(:ConsoAnSG,0);
	end if;
end;

      function CF_G4_ConsoMensFormula return Number is
begin
