function.textSource:
      function AfterPForm return boolean is
      l_where_dpcode  VARCHAR2(100) := ' ';
      l_param6        VARCHAR2(5)    := '';
begin
  -- gestion du PARAM6 contenant la valeur du sous projet ICPI.
	
	-- On enlève tous les espaces et on passe la chaîne en majuscules
  	l_param6 := UPPER(RTRIM(LTRIM(:P_PARAM6)));
	
	------------------------------------------------------------------------
 	-- Conditions WHERE sur icpi
 	------------------------------------------------------------------------
 		IF (l_param6 is not null) THEN
			  l_where_dpcode := ' AND pi.ICPI = '''||l_param6||'''  ';
        :P_WHERE := l_where_dpcode;
		END IF;

 	  --srw.message(1, ':P_WHERE : ' || l_where_dpcode);
 	  
  RETURN TRUE;
end;

      function B_DATSTATUT2FormatTrigger return boolean is
begin

