function.textSource:
      function AfterPForm return boolean is
      l_where_dpcode    VARCHAR2(100) := ' ';
      l_where_datdebex  VARCHAR2(100) := ' ';
      l_param6          VARCHAR2(5)   := '';
      annee             VARCHAR2(4)   := '';
BEGIN
	-- paramètre :
	--        P_PARAM6 : variable où se trouve le DPCODE saisi à l'écran
	
	-- On enlève tous les espaces et on passe la chaîne en majuscules
	l_param6 := UPPER(RTRIM(LTRIM(:P_PARAM6)));
	
	------------------------------------------------------------------------
 	-- Conditions WHERE sur dpcode
 	------------------------------------------------------------------------
		
		-- Si la valeur du DPCODE est égale à un dossier projet
		IF (l_param6 is not null) THEN
			  l_where_dpcode := ' AND dp.DPCODE = to_number('''||l_param6||''')  ';
			  --srw.message(1, 'Dossier Projet : ' || l_param6);
		END IF;	
		-- Récupération des dates de traitement
    SELECT TO_CHAR(datdebex,'YYYY')    INTO  annee    FROM datdebex;
			
 	  --srw.message(2, 'Exercice : ' || annee);
 	  
 	  l_where_datdebex := ' AND to_char(bud.ANNEE(+)) = '''||annee||''' AND to_char(con.ANNEE(+)) = '''||annee||'''  ';
 	  --srw.message(3, 'l_where_datdebex : ' || l_where_datdebex);
 
 	  :P_WHERE := l_where_dpcode || l_where_datdebex;
 	  --srw.message(4, 'P_WHERE : ' || :P_WHERE);
 	  
  RETURN TRUE;
end;

      -- ----------------------------------------------------------------------
-- Retourn la date de ma dernière mensuelle 
-- ----------------------------------------------------------------------
function CF_DERTRAIT return Date 
is d1 date;
begin
  select moismens into d1 from datdebex;
  return (d1);
end;

      -- ----------------------------------------------------------------------
-- Retourn l'année d'exercice en cours
-- ----------------------------------------------------------------------
function CF_ANNEE return Date 
is d date;
begin
  select datdebex into d from datdebex;
  return (d);
end;

      function P_WHEREValidTrigger return boolean is
begin
  
  return (TRUE);
end;
