      ------------------------------------------------------------------------------------------
-- Rôle : Construire un Where conditionnel. La chaine construite sera affectée au parametre
-- de P_WHERE. 
------------------------------------------------------------------------------------------
function AfterPForm return boolean is
   l_annee0  VARCHAR2(10);
   l_annee1  VARCHAR2(10);
   l_annee2  VARCHAR2(10);
   l_dateMoisPred date;
   l_Text varchar2(7);
   l_perim_me	VARCHAR2(1000) := '';
   p_dpg VARCHAR2(10) := '';
   
begin
	
	l_perim_me := pack_global.lire_perime(:P_GLOBAL);
	
	-- On anlève tous les espaces
	p_dpg := RTRIM(LTRIM(:P_PARAM6));
	p_dpg := replace(p_dpg,'*','%') ;
	
   l_dateMoisPred := ADD_MONTHS(sysdate,-1) ;
   -- l_annee0 := 2000;
   l_annee0 := to_char(l_dateMoisPred,'YYYY');
   l_annee1 := to_number(l_annee0) + 1;
   l_annee2 := to_number(l_annee0) + 2;

   :p_where := ' and budg.annee (+) = ' || l_annee0 ;                  
   :p_where := :p_where || ' and budg1.annee (+) = ' || l_annee1 ;
   :p_where := :p_where || ' and budg2.annee (+) = ' || l_annee2 ;
   :p_where := :p_where || ' and conso.annee (+) = ' || l_annee0 ;

	-- Filtre perimetre ME
	:p_where := :p_where || ' AND lb.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 
	
	-- Filtre DPG
   if p_dpg is not null then 
     	:p_where := :p_where || ' and  to_char(lb.codsg,''FM0000000'') like ''' || p_dpg || '''';
   end if;
 
   --srw.message(1,:P_where);
     
   return (TRUE);
end;

      -- -------------------------------------------------------------------------
-- Supprime des cartactres '*', blancs ou '$' se trouvant à la fin de p_text 
-- Retourne la chaine resultante.
----------------------------------------------------------------------------
FUNCTION RtrimEtoile(P_Text In VARCHAR2) RETURN varchar2 IS
  meta VARCHAR2(1);
  L_Text varchar2(7);
BEGIN
  L_Text := P_Text;
  meta := substr(P_Text, NVL(length(P_Text), 0),1);
  if (meta = ' ') or (meta = '*') or (meta = '$') then
      L_Text := rtrim(P_Text,meta);   
  end if;
    
  return(L_Text);
END;

      -- ----------------------------------------------------------------------
-- Retourn la (date systeme - 1 Mois)
-- ----------------------------------------------------------------------
function CF_LastMonthDate return Date is
d date;
begin
  select ADD_MONTHS(sysdate,-1) into d from dual;
  return(d);
end;

      -- ----------------------------------------------------------------------
-- Retourn CF_AN0 + 1 (cf cf_an0)
-- ----------------------------------------------------------------------
function CF_AN1 return VARCHAR2 is
  YearOflastMonth number(4);
begin
  select to_char(ADD_MONTHS(sysdate,-1), 'YYYY') into YearOflastMonth from dual;
  return (to_char(YearOflastMonth + 1));
  
end;

      -- ----------------------------------------------------------------------
-- Retourn l'année du (date systeme - 1 Mois)
-- ----------------------------------------------------------------------
function CF_AN0 return VARCHAR2 is
  YearOflastMonth number(4);
begin
  select to_char(ADD_MONTHS(sysdate,-1), 'YYYY') into YearOflastMonth from dual;
  return (to_char(YearOflastMonth));
end;

      function CF_AN2Formula return VARCHAR2 is
  YearOflastMonth number(4);
begin
  select to_char(ADD_MONTHS(sysdate,-1), 'YYYY') into YearOflastMonth from dual;
  return (to_char(YearOflastMonth + 2));
  
end;
