-------------------------------------------------------------------------------- -- Query Etat $RESFACT -- Facturation attendue des Ressources -- DESFORMAT = g:\commun\bip_dev\reports\bip088.prt -- Modif 26/7/2000 : utilisation de datdebex.moismens -- CMA 702 26/11/2010 : Ajout des ressources avec une situ ress mais un jh à 0 hors forfait -------------------------------------------------------------------------------- SELECT DECODE (SUBSTR(LPAD(:P_PARAM6, 7, '0'),6,2),'**', SUBSTR(TO_CHAR(pro.divsecgrou, 'FM0000000'), 1, 5) , str.codsg) dc_codpole, pro.societe dc_societe, pro.tires dc_tires, pro.rnom dc_rnom, pro.cout dc_cout_jour, SUM(pro.cusag) dc_nbjour_trav, DECODE (SUBSTR(LPAD(:P_PARAM6, 7, '0'),6,2),'**', MAX(str.sigdep || '/' || str.sigpole), MAX(str.libdsg) ) dc_libpole, situ.montant_mensuel dc_montant_mens FROM situ_ress situ, proplus pro, struct_info str WHERE pro.societe != 'SG..' AND situ.montant_mensuel is null AND ( (pro.qualif NOT IN ('GRA', 'MO', 'MO ', ' MO')) OR (pro.qualif IS NULL) ) AND ( (pro.aist NOT IN ('FORMAT', 'ABSDIV', 'CONGES', 'MOBILI','RTT')) OR (pro.aist IS NULL) ) AND TRUNC(pro.cdeb, 'MONTH') = ( SELECT TRUNC(moismens, 'MONTH') FROM datdebex ) -------------------------------------------------------------- -- Jointure avec struct_info pour avoir les libellés DPG -------------------------------------------------------------- AND pro.divsecgrou = str.codsg (+) -------------------------------------------------------------- -- jointure avec la table des situations -------------------------------------------------------------- AND pro.tires=situ.ident AND (situ.datsitu <= pro.cdeb AND (situ.datdep IS NULL OR situ.datdep > pro.cdeb)) -------------------------------------------------------------- -- Restriction suivant Parametre Dpt/Pôle saisi -------------------------------------------------------------- &P_WHERE GROUP BY SUBSTR(TO_CHAR(pro.divsecgrou, 'FM0000000'), 1, 5), str.codsg, pro.societe, pro.tires, pro.rnom, pro.cout,situ.montant_mensuel
UNION SELECT DECODE (SUBSTR(LPAD(:P_PARAM6, 7, '0'),6,2),'**', SUBSTR(TO_CHAR(situ.codsg, 'FM0000000'), 1, 5) , str.codsg) dc_codpole, situ.soccode dc_societe, situ.ident dc_tires, ress.rnom dc_rnom, 0 dc_cout_jour, 0 dc_nbjour_trav, DECODE (SUBSTR(LPAD(:P_PARAM6, 7, '0'),6,2),'**', MAX(str.sigdep || '/' || str.sigpole), MAX(str.libdsg) ) dc_libpole, situ.montant_mensuel dc_montant_mens FROM situ_ress situ, ressource ress, struct_info str, datdebex dx WHERE situ.soccode != 'SG..' AND situ.montant_mensuel is not null -------------------------------------------------------------- -- Jointure avec struct_info pour avoir les libellés DPG -------------------------------------------------------------- AND situ.codsg = str.codsg (+) -------------------------------------------------------------- -- jointure avec la table des situations -------------------------------------------------------------- AND ress.ident=situ.ident AND (situ.datsitu <= dx.moismens AND (situ.datdep IS NULL OR situ.datdep > dx.moismens)) -------------------------------------------------------------- -- Restriction suivant Parametre Dpt/Pôle saisi -------------------------------------------------------------- &P_WHERE_F12 GROUP BY SUBSTR(TO_CHAR(situ.codsg, 'FM0000000'), 1, 5), str.codsg, situ.soccode, situ.ident, ress.rnom, situ.montant_mensuel UNION SELECT DECODE (SUBSTR(LPAD('0161614', 7, '0'),6,2),'**', SUBSTR(TO_CHAR(situ.codsg, 'FM0000000'), 1, 5) , str.codsg) dc_codpole, situ.soccode dc_societe, situ.ident dc_tires, ress.rnom dc_rnom, situ.cout dc_cout_jour, 0 dc_nbjour_trav, DECODE (SUBSTR(LPAD('0161614', 7, '0'),6,2),'**', MAX(str.sigdep || '/' || str.sigpole), MAX(str.libdsg) ) dc_libpole, situ.montant_mensuel dc_montant_mens FROM situ_ress situ, ressource ress, struct_info str, datdebex dx WHERE situ.soccode != 'SG..' AND situ.montant_mensuel is null -------------------------------------------------------------- -- Jointure avec struct_info pour avoir les libellés DPG -------------------------------------------------------------- AND situ.codsg = str.codsg (+) -------------------------------------------------------------- -- jointure avec la table des situations -------------------------------------------------------------- AND ress.ident=situ.ident AND (situ.datsitu <= dx.moismens AND (situ.datdep IS NULL OR situ.datdep > dx.moismens)) -------------------------------------------------------------- -- Restriction suivant Parametre Dpt/Pôle saisi -------------------------------------------------------------- &P_WHERE_F12 -------------------------------------------------------------- -- Ceux qui n'ont pas de JH réalisé sur ce mois -------------------------------------------------------------- AND NOT EXISTS (SELECT factpid from proplus pro where pro.tires=situ.ident AND TRUNC(pro.cdeb, 'MONTH') = ( SELECT TRUNC(moismens, 'MONTH') FROM datdebex ))
GROUP BY SUBSTR(TO_CHAR(situ.codsg, 'FM0000000'), 1, 5), str.codsg, situ.soccode, situ.ident, ress.rnom, situ.cout, situ.montant_mensuel ;

function.textSource:
      function AfterPForm return boolean is

      l_param VARCHAR2(10);
      l_perim_me	VARCHAR2(1000) := '';

begin
	
	-- Filtre du perim ME
	l_perim_me := pack_global.lire_perime(:P_GLOBAL);		
	:P_WHERE :=' AND pro.divsecgrou IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 
	:P_WHERE_F12 := ' AND situ.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 
	
	IF LPAD(:P_PARAM6, 7, '0') != '*******' THEN
	
				-- ajout de '0' a gauche sur 7 caracteres + suppression des '*'
	  l_param := rtrim(rtrim(LPAD(:P_PARAM6, 7, '0'),'*')) ;
		:P_WHERE := :P_WHERE || ' and  TO_CHAR(pro.divsecgrou, ''FM0000000'') like ''' || l_param ||'%'' ';
		:P_WHERE_F12 := :P_WHERE_F12 || ' AND TO_CHAR(situ.codsg, ''FM0000000'') like ''' || l_param ||'%'' ';
		
	END IF;

	
 
--	if ( :P_WHERE is not NULL)
--	then
		--srw.message(1, :P_WHERE);
	--end if;
	
	return (TRUE);

end;

      function CF_COUT_HT_RESFormula return Number is
	l_res NUMBER(25,2) := 0;
begin
  	if :dc_montant_mens is null then
  		l_res := :dc_cout_jour * :dc_nbjour_trav;
    	
  	else
  		l_res := :dc_montant_mens;
  	end if;
  return(l_res);
end;

      function CF_COUT_TTC_RESFormula return Number is
	l_res NUMBER(25,2) := 0;
	
begin
  	

	l_res := :CF_COUT_HT_RES  *(1 + :cf_tva/100) ;
    	return(l_res);
end;

      function M_TOT_SOC_FTRFormatTrigger return boolean is
begin
  IF ( :CS_COUNT_NBRES_SOC > 1 )
  THEN
     return (TRUE);
  ELSE
     return (FALSE);
  END IF;
end;

      function M_TOT_GEN_FTRFormatTrigger return boolean is
begin
  IF ( :CS_NBPOLE_TOT > 1 )
  THEN  
     return (TRUE);
  ELSE
     return (FALSE);
  END IF;
end;

      function F_GROUPEFormatTrigger return boolean is
Text varchar2(100);
begin
   	IF SUBSTR( LPAD(:P_PARAM6, 7, '0'),6,2)='**' THEN
		Text := 'POLE';
	ELSE
		Text := 'GROUPE';
	END IF;

   SRW.SET_FIELD_CHAR(0,Text); 
  return (TRUE);
end;

      function CF_COUT_HTRFormula return Number is
l_taux number(9,2);
l_cout_htr number(12,2);
begin

	begin
		select taux/100 into l_taux
		 from taux_recup 
		where annee = to_number(to_char(sysdate,'yyyy')) 
		and filcode ='01';

	exception
		WHEN NO_DATA_FOUND THEN
			l_taux:=0;
	end;
l_cout_htr := :CF_COUT_HT_RES + (:cf_tva  - l_taux * :cf_tva )/100*(:CF_COUT_HT_RES);
return l_cout_htr;


end;

      function CF_1Formula return Number is
l_tva number(9,2);
begin
  	select tva into l_tva
	from tva where datetva = (select max(datetva) from tva);
   
return(l_tva);
end;

      function T_fact12FormatTrigger return boolean is
begin
  if :dc_montant_mens is null then
  return (FALSE);
  else return (TRUE);
  end if;
end;

      function F_dc_cout_jourFormatTrigger return boolean is
begin
  if :dc_montant_mens is null then
  return (TRUE);
  else return (FALSE);
  end if;
end;
