function.textSource:
      function AfterPForm return boolean is
      l_trim_p_mois_annee VARCHAR2(10);
      l_p_code_dpg VARCHAR2(10);
      l_param VARCHAR2(10);
      l_p_ress VARCHAR2(10);
      l_perim_me	VARCHAR2(1000) := '';
      
      ------------------------------------------------------------------------
      -- Conditions WHERE sur les différents paramètres
      -----------------------------------------------------------------------
      l_where_annee VARCHAR2(500) := '';
      l_where_dpg VARCHAR2(1000) := '';
      l_where_ress VARCHAR2(500) := '';
      l_where_annee_1 VARCHAR2(500) := '';
      l_where_dpg_1 VARCHAR2(1000) := '';
      l_where_ress_1 VARCHAR2(500) := '';
      l_where_me	VARCHAR2(1000) := '';
      l_where_me_1	VARCHAR2(1000) := '';
      
      ----------------------------------------------------------------------

      l_fmt_ddmmyyyy CONSTANT VARCHAR2(10) := 'DD/MM/YYYY';
      l_fmt_yyyy CONSTANT VARCHAR2(4) := 'YYYY';
      
      l_ch_01_01_annee VARCHAR2(10);
      l_ch_31_12_annee VARCHAR2(10);

      l_annee_traitee VARCHAR2(4) := '1900';

      l_car_blanc  CONSTANT VARCHAR2(1) := ' ';   -- Caractère blanc

      l_car_topage_ouvert CONSTANT VARCHAR2(1) := 'O';
      l_car_topage_ferme  CONSTANT VARCHAR2(1) := 'F'; -- BPO : Modification de 'N' à 'F' car TOPFER = F pour fermé
      l_car_like CONSTANT VARCHAR2(1) := '%';


      l_ch_00 CONSTANT VARCHAR2(2) := '00'; 
      l_ch_0000 CONSTANT VARCHAR2(4) := '0000'; 
      l_ch_99 CONSTANT VARCHAR2(2) := '99'; 
      l_ch_9999 CONSTANT VARCHAR2(4) := '9999'; 

	
      l_mois_annee_traitee VARCHAR2(7) := '01/1900';
      l_mois_traite VARCHAR2(2) := '12';
      l_derjour_du_mois_traite VARCHAR2(2) := '31';
      l_date_inf_a_considerer VARCHAR2(10) := '01/01/1900';	
      l_date_sup_a_considerer VARCHAR2(10) := '31/12/2999';  
      
      l_liste   VARCHAR2(500);

begin

/****************************************************************************
*	TRAITEMENT PRELIMINAIRE SUR LES PARAMETRES
* Pour le Menu HISTORIQUE, les paramètres envoyés par IE4 sont :
*	P_PARAM6 = Date au format MM/AAAA à prendre en compte (Obligatoire)
*	P_PARAM7 = Code DPG Sur 7 caractères (Optionnel)
*	P_PARAM8 = Code Ressource (Optionnel)
*	Ce sont les paramètres attendus par le Report!!!
*
* Pour le Menu MENSUELLE, les paramètres envoyés par IE4 sont :
*	P_PARAM6 = Code DPG sur 7 caractères (normalement)
*
* CONSEQUENCE :
*	Dans le cas de la MENSUELLE, fait en sorte que :
*		P_PARAM7 soit égal à P_PARAM6**
*		P_PARAM6 soit vide : Ainsi on prendra la date dans DATDEBEX 
****************************************************************************/
  	IF ( NVL(LENGTH(:P_PARAM6), 0) = 5 ) THEN
		--srw.message(1, 'P_PARAM6 AVANT : ' || '[' || :P_PARAM6 || ']');
		:P_PARAM7 := :P_PARAM6 || '**';
		:P_PARAM6 := '';
		--srw.message(1, 'P_PARAM6 APRES : ' || '[' || :P_PARAM6 || ']');
		--srw.message(1, 'P_PARAM7 APRES : ' || '[' || :P_PARAM7 || ']');
	ELSE
		IF ( NVL(LENGTH(:P_PARAM6), 0) = 6 ) THEN
			:P_PARAM7 := '0' || :P_PARAM6;
		ELSIF (( NVL(LENGTH(:P_PARAM6), 0) = 7 ) AND (INSTR(:P_PARAM6, '/') = 0)) THEN
			:P_PARAM7 := :P_PARAM6;
		END IF;
	END IF;


/**********************************************************************************
*	DEBUT DU TRAITEMENT PROPREMENT DIT 
***********************************************************************************/
	l_trim_p_mois_annee := ltrim(rtrim(:P_PARAM6)); /* format : MM/YYYY */

  l_p_code_dpg := LPAD(:P_PARAM7, 7, '0');
	
		IF :P_PARAM8='Toute' THEN
		-- cas existant dans Saisie des realises/Historique/Ressources
 		l_liste :=pack_global.lire_globaldata(:P_GLOBAL).chefprojet;
--srw.message(1, '[liste] : ' || l_liste);
			l_where_ress  := ' ((INSTR('''||l_liste||''',TO_CHAR(pro.cpident,''FM00000''))>0)
		  		or(INSTR('''||l_liste||''',TO_CHAR(pro.tires,''FM00000''))>0))';
--srw.message(1, '[liste] : ' || l_where_ress);
		  l_where_ress_1  := ' ((INSTR('''||l_liste||''',TO_CHAR(situ.cpident,''FM00000''))>0)
		  		or(INSTR('''||l_liste||''',TO_CHAR(situ.ident,''FM00000''))>0))';
--srw.message(1, '[liste] : ' || l_where_ress_1);
 ELSE
 	l_p_ress := :P_PARAM8; 
	
      	------------------------------------------------------------------------
      	-- Conditions WHERE sur ressource
      	-----------------------------------------------------------------------
	
        if ( (NVL(LENGTH(l_p_ress), 0)!= 0) AND (l_p_ress != '00000') ) 
 				then 
					l_where_ress  := ' pro.tires = ' || l_p_ress ;
					l_where_ress_1  := ' situ.ident = ' || l_p_ress ;
					--srw.message(1, '[liste] : ' || l_where_ress_1);
        end if;
 END IF;

	      ------------------------------------------------------------------------
      	-- Conditions WHERE sur Année
      	-----------------------------------------------------------------------
	
	if ( NVL(LENGTH(l_trim_p_mois_annee), 0) = 7 ) AND (INSTR(l_trim_p_mois_annee, '/') > 0)
        then
             	----------------------------------------------------------------
             	-- Cas année saisie au format MM/YYYY : On doit traiter année saisie!
             	----------------------------------------------------------------
		l_annee_traitee := substr(l_trim_p_mois_annee, 4, 4);
		l_mois_traite   := substr(l_trim_p_mois_annee, 1, 2);
	else
             	----------------------------------------------------------------
             	-- Cas année non saisie : On doit traiter date mensuelle de la table datdebex!
		-- On positionne aussi P_PARAM6 avec MM/AAAA de datdebex pour que le 
		-- Query de l'état fonctionne bien
             	----------------------------------------------------------------	 
  		SELECT TO_CHAR(moismens, 'MM/YYYY') INTO l_mois_annee_traitee FROM datdebex;

		l_annee_traitee := substr(l_mois_annee_traitee, 4, 4);
		l_mois_traite   := substr(l_mois_annee_traitee, 1, 2);
			
	--	SELECT TO_CHAR (datdebex,'YYYY') INTO l_annee_traitee FROM datdebex;


		:P_PARAM6 := l_mois_annee_traitee; 
	end if;

l_derjour_du_mois_traite := TO_CHAR( LAST_DAY(TO_DATE('01' || '/' || l_mois_traite  || '/' || l_annee_traitee, 'DD/MM/YYYY')), 'DD' );
	

	l_date_inf_a_considerer := '01/01/' || l_annee_traitee;
	l_date_sup_a_considerer := l_derjour_du_mois_traite || '/' || l_mois_traite || '/' || l_annee_traitee;

	l_where_annee := ' pro.cdeb BETWEEN  TO_DATE( '''  ||  l_date_inf_a_considerer  || ''' , ' ;
	l_where_annee := l_where_annee || ' ''' || l_fmt_ddmmyyyy || ''' ) ' ; 
	l_where_annee := l_where_annee || ' AND   TO_DATE( '''  ||  l_date_sup_a_considerer || ''' , ' ;
	l_where_annee := l_where_annee || ' ''' || l_fmt_ddmmyyyy || ''' ) ' ; 
	
	l_where_annee_1 := ' situ.datsitu <= TO_DATE( ''' ||  l_date_sup_a_considerer  || ''' , ' ;
	l_where_annee_1 := l_where_annee_1 || ' ''' || l_fmt_ddmmyyyy || ''' ) ' ; 
	l_where_annee_1 := l_where_annee_1 || ' AND (situ.datdep >= TO_DATE( ''' ||  l_date_inf_a_considerer || ''' , ' ;
	l_where_annee_1 := l_where_annee_1 || ' ''' || l_fmt_ddmmyyyy || ''' ) ' ; 
	l_where_annee_1 := l_where_annee_1 || ' OR situ.datdep IS NULL) ';

	------------------------------------------------------------------------
      	-- Conditions WHERE sur perimetre ME
	-----------------------------------------------------------------------

	l_perim_me := pack_global.lire_perime(:P_GLOBAL);		
	l_where_me := ' pro.divsecgrou IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 
	l_where_me_1 := ' situ.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 


	------------------------------------------------------------------------
      	-- Conditions WHERE sur dpg
      	-----------------------------------------------------------------------

	IF ( (l_p_code_dpg IS NULL) 
			OR 
             l_p_code_dpg = '*******'
		OR  
	     (l_p_code_dpg = l_car_blanc || l_car_blanc || l_car_blanc || l_car_blanc || l_car_blanc || l_car_blanc || l_car_blanc)
	   ) THEN
	       -----------------------------------------------------------------------------------------------------------------------------------------
	     	 -- Si Code DPG vide ou égal à 7 étoiles, Considérer tous les DPG avec code topé à 'O'
	     	 -- Modifié par DDI le 14/10/2005 - Fiche TD N°276 : Les historiques doivent toujours être complets. Même sur les DPG qui ont été fermés .
		     -- l_where_dpg := ' str.topfer =   ''' || l_car_topage_ouvert || ''' )';
		     -- l_where_dpg_1 := ' str.topfer =   ''' || l_car_topage_ouvert || ''' )';
		     -----------------------------------------------------------------------------------------------------------------------------------------  
		     l_where_dpg   := ' ( str.topfer =   ''' || l_car_topage_ouvert || ''' OR str.topfer =   ''' || l_car_topage_ferme || ''' )';
		     l_where_dpg_1 := ' ( str.topfer =   ''' || l_car_topage_ouvert || ''' OR str.topfer =   ''' || l_car_topage_ferme || ''' )';

	ELSE
		
		-- ajout de '0' a gauche sur 7 caracteres + suppression des '*'
		-- QC 1281
	  l_param := rtrim(rtrim(LPAD(l_p_code_dpg, 7, '0'),'*')) ;
		l_where_dpg := ' TO_CHAR(pro.divsecgrou, ''FM0000000'') like ''' || l_param || '%'' ';
		l_where_dpg_1 := ' TO_CHAR(situ.codsg, ''FM0000000'') like ''' || l_param || '%'' ';


	END IF;

      	------------------------------------------------------------------------
      	-- Condition WHERE finale 
      	-----------------------------------------------------------------------
	:P_WHERE := '';
	:P_WHERE_1 := '';
	
	if ( NVL(LENGTH(l_where_ress), 0) != 0 ) then 
		:P_WHERE := :P_WHERE || ' AND ( ' || l_where_ress || ' ) ';
		:P_WHERE_1 := :P_WHERE_1 || ' AND ( ' || l_where_ress_1 || ' ) ';
	end if;

	if ( NVL(LENGTH(l_where_annee), 0) != 0 ) then 
		:P_WHERE := :P_WHERE || ' AND ( ' || l_where_annee || ' ) ';
		:P_WHERE_1 := :P_WHERE_1 || ' AND ( ' || l_where_annee_1 || ' ) ';
	end if;
	
	if ( NVL(LENGTH(l_where_dpg), 0) != 0 ) then 
		:P_WHERE := :P_WHERE || ' AND  ' || l_where_dpg || '  ';
		:P_WHERE_1 := :P_WHERE_1 || ' AND  ' || l_where_dpg_1 || '  ';
	end if;

	if ( NVL(LENGTH(l_where_me), 0) != 0 ) then 
		:P_WHERE := :P_WHERE || ' AND  ' || l_where_me|| '  ';
		:P_WHERE_1 := :P_WHERE_1 || ' AND  ' || l_where_me_1 || '  ';
	end if;
	
--	if ( NVL(LENGTH(:P_WHERE), 0) != 0 )
--	then
		--srw.message(1, '[P_WHERE] : ' || :P_WHERE);
	--end if;
	
--	if ( NVL(LENGTH(:P_WHERE_1), 0) != 0 )
--	then
		--srw.message(1, '[P_WHERE_1] : ' || :P_WHERE_1);
--	end if;
	
	return (TRUE) ;  

end;

function M_SOUS_TACHEFormatTrigger return boolean is begin
IF ( (:dc_conso_01 = 0) AND (:dc_conso_02 = 0) AND (:dc_conso_03 = 0) AND (:dc_conso_04 = 0) AND (:dc_conso_05 = 0) AND (:dc_conso_06 = 0) AND (:dc_conso_07 = 0) AND (:dc_conso_08 = 0) AND (:dc_conso_09 = 0) AND (:dc_conso_10 = 0) AND (:dc_conso_11 = 0) AND (:dc_conso_12 = 0) ) THEN RETURN(FALSE); ELSE return (TRUE); END IF;
end;

