      function F_P_PARAM1FormatTrigger return boolean is
Text varchar2(100);
begin

	Text := :dc_libdsg ||' - '||to_char(:dc_coddpg, 'FM0000000') ;  
	SRW.SET_FIELD_CHAR(0,Text); 
	
--  end if;
  
  return (TRUE);
end;

