      function R_SOUS_TACHEFormatTrigger return boolean is
begin
	--differenciation des absences non facturees
 IF (:dc_societe='SG..')AND (:dc_type=7)  THEN
 srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='gray4';
 srw.set_attr (0,srw.attr);
 END IF;
 
 IF (:dc_societe!='SG..')AND (:dc_type=7)AND (:dc_soustache not in ('SEMINA','FORFAC','DEMENA','CLUBUT','COLOQU'))  THEN
 srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='gray4';
 srw.set_attr (0,srw.attr);
  END IF;
  return (true);
  
end;

