      function AfterPForm return boolean is
l_annee varchar2(10);
l_moismens varchar2(10);
l_format_date varchar2(10);
l_mois_annee varchar2(10);
l_listeChefsProjets varchar2(5000);

 begin
	l_listeChefsProjets := pack_global.lire_globaldata(:P_GLOBAL).chefprojet;
	:P_WHERE := ' AND INSTR('''|| l_listeChefsProjets ||''', TO_CHAR(pro.cpident, ''FM00000'') ) > 0';
 	
	if (:P_PARAM7 !='Tous') then
		:P_WHERE :=  :P_WHERE ||' AND pro.cpident='||:P_PARAM7;
	end if;
	
	l_format_date:='DD/MM/YYYY';

  SELECT to_char(TRUNC(moismens, 'YEAR'),'dd/mm/yyyy'),to_char(LAST_DAY(moismens),'dd/mm/yyyy'),
	 TO_CHAR(moismens, 'MM/YYYY')
	 INTO l_annee, l_moismens, l_mois_annee FROM datdebex;

 	:P_PARAM6 := l_mois_annee; 


	:P_WHERE := :P_WHERE ||' and pro.cdeb BETWEEN TO_DATE('''||l_annee||''','''||l_format_date||''') AND TO_DATE(''';
	:P_WHERE := :P_WHERE ||l_moismens ||''','''||l_format_date||''')';

	if ( NVL(LENGTH(:P_WHERE), 0) != 0 ) then
		--srw.message(1, '[P_WHERE] : ' || :P_WHERE||' ,PARAM7:'||:P_PARAM7);
	end if;
	return (TRUE) ;  
end;

      function B_FORFACFormatTrigger return boolean is
begin
  IF ( (:dc_soustache  IS NULL) OR (:dc_soustache != 'FORFAC') )THEN
  	RETURN(FALSE);
  ELSE
  	return (TRUE);
  END IF;
end;

