function.textSource:
      ------------------------------------------------------------------------------------------
-- Rôle : Construire un Where conditionnel.
------------------------------------------------------------------------------------------
function AfterPForm return boolean is
BEGIN
	 -- On vire tous les espaces
	 :P_PARAM6 := RTRIM(LTRIM(:P_PARAM6));
	 :P_PARAM7 := RTRIM(LTRIM(:P_PARAM7));
	 :P_PARAM8 := RTRIM(LTRIM(:P_PARAM8));
	 :P_PARAM9 := RTRIM(LTRIM(:P_PARAM9));
	
   -- Informations pour l'habilitation par référentiels
   :P_habili_DP := pack_global.lire_doss_proj(:P_GLOBAL);
   :P_habili_P  := pack_global.lire_projet(:P_GLOBAL);
   :P_habili_A  := pack_global.lire_appli(:P_GLOBAL);
   :p_menu      := pack_global.lire_globaldata(:P_GLOBAL).menutil;
	 -- Initialisation de P_WHERE
	 :P_WHERE := '';
  
 
  IF (:p_menu = 'REF') THEN
  	
 
				-- On rempli la clause qui limite les résultats
			  -- Fonction de l'habilitation par référentiel
			  -- Dossier Projet demandé
			  IF (:P_PARAM6='DP') THEN
			  	:P_WHERE := 'AND ((INSTR(UPPER(:P_habili_DP), ''TOUS'') > 0)
			  								 OR (INSTR(lpad(:P_habili_DP,5,0), r.codgroup) > 0)
			  							)';
			  	:p_param8 := null;
			  	:p_param9 := null;
			  -- Projet demandé
			  ELSIF (:P_PARAM6='P') THEN
			  	-- On fait aussi le contrôle que sur le projet principal (icpir) comme dans la proc
					:P_WHERE := 'AND ((INSTR(UPPER(:P_habili_P), ''TOUS'') > 0)
												 OR (INSTR(UPPER(:P_habili_DP), ''TOUS'') > 0)
												 OR	(INSTR(:P_habili_P, r.codgroup) > 0)
												 OR (INSTR(:P_habili_P, p.icpir) > 0)
												 OR (INSTR(:P_habili_DP, TO_CHAR(lb.dpcode, ''FM00000'')) > 0 )
											)';
					:p_param7 := null;
			  	:p_param9 := null;
				-- Application demandée
			  ELSIF (:P_PARAM6='A') THEN
			  	-- On fait aussi le contrôle sur l'application principale comme dans la proc
					:P_WHERE := 'AND ((INSTR(UPPER(:P_habili_A), ''TOUS'') > 0)
												 OR (INSTR(UPPER(:P_habili_P), ''TOUS'') > 0)
												 OR (INSTR(UPPER(:P_habili_DP), ''TOUS'') > 0)
												 OR (INSTR(:P_habili_A, r.codgroup) > 0)
												 OR (INSTR(:P_habili_A, a.acdareg) > 0)
												 OR (INSTR(:P_habili_P, lb.icpi) > 0) 
												 OR (INSTR(:P_habili_DP, TO_CHAR(lb.dpcode, ''FM00000'')) > 0 )
				     					 )';
				   :p_param7 := null;
			  	:p_param8 := null;
				END IF; 
			
			
				-- Si un élément spécifique est sélectionné pour l'habili par ref
				-- On le met dans P_PARAM6
			  IF :P_PARAM6='DP' AND :P_PARAM7 = 'TOUS' THEN
			  	 :P_PARAM7 := :P_PARAM6;
			  ELSIF :P_PARAM6='P' AND :P_PARAM8 = 'TOUS' THEN
			  	:P_PARAM8 := :P_PARAM6;
			  ELSIF :P_PARAM6='A' AND :P_PARAM9 = 'TOUS' THEN
			  	 :P_PARAM9 := :P_PARAM6;
	
	
			  END IF;
  
  	END IF;
  
  
  
  
  
  
  RETURN TRUE;
end;

      function BeforeReport return boolean is
begin
  -- Rechercher les donnees et les inserer dans la table tmpreftrans
 	if (:p_menu = 'REF') then
	
		:p_numseq := pack_RefPCM.Select_DataReftrans(:p_param8,:p_param9,:p_param7, :P_GLOBAL);  
	end if;
  if (:p_numseq != 0) then
    return (true);
  else 
    return (false);
  end if;
end;

      function AfterReport return boolean is
   l_ret boolean ;
begin
  l_ret := pack_Refpcm.Delete_Donnees(:p_numseq);  
  return (l_ret);
end;

      function CF_LibTypeFormula return Char is
begin
   if (:p_menu = 'REF') then
   		return  pack_RefTrans.Titre3(:P_PARAM8,:P_PARAM9,:P_PARAM7);
   end if;
end;

      function CF_LastMonthDateFormula return Date is
d date;
begin
  select ADD_MONTHS(sysdate,-1) into d from dual;
  return(d);
end;

      -- ----------------------------------------------------------------------
-- Retourn CF_AN0 + 1 (cf cf_an0)
-- ----------------------------------------------------------------------
function CF_AN1 return VARCHAR2 is
  YearOflastMonth number(4);
begin
  select to_char(ADD_MONTHS(sysdate,-1), 'YYYY') into YearOflastMonth from dual;
  return (to_char(YearOflastMonth + 1));
end;

      -- ----------------------------------------------------------------------
-- Retourn l'année du (date systeme - 1 Mois)
-- ----------------------------------------------------------------------
function CF_AN0 return VARCHAR2 is
  YearOflastMonth number(4);
begin
select to_char(ADD_MONTHS(sysdate,-1), 'YYYY') into YearOflastMonth from dual;
  return (to_char(YearOflastMonth));
end;

      function B_18FormatTrigger return boolean is
nbLignesMax NUMBER(10);
begin
	nbLignesMax := pack_reftrans.Get_Nb_Lignes_clicode(:codgroup,:p_numseq, :codmo);
  IF (:CS_NbLignes =nbLignesMax) THEN
  	return (FALSE);
  ELSE
  	return (TRUE);
  END IF;
end;

      function CF_LIB_TitreFormula return Char is
begin
IF (:P_PARAM7 = 'DP' or :P_PARAM8 = 'P') then
      RETURN 'Tous ceux de l''habilitation';
  ELSIF :P_PARAM9 = 'A' THEN
  	RETURN 'Toutes celles de l''habilitation';
  ELSE 
  	RETURN :P_PARAM6 || ' ' || pack_reftrans.TITRE4(:P_PARAM7,:P_PARAM8,:P_PARAM9);
  END IF;
end;
