      function CF_1FORMULA0017 return Number is
dispo_htr number(8,2);
begin
dispo_htr := :budget - :CS_engage;
return dispo_htr; 
end;

      function CF_2Formula return Number is

-- Declaration des variables
t_recup taux_recup.taux%TYPE;
t_recup_max taux_recup.taux%TYPE;

begin
	
select min(taux) into t_recup
from taux_recup tr,
centre_activite c,
centre_activite c2
where 
c2.codcamo=:p_param6
and ((c2.codcamo = c.codcamo)
or (c.caniv1 = c2.codcamo)
or (c.caniv2 = c2.codcamo)
or (c.caniv3 = c2.codcamo)
or (c.caniv4 = c2.codcamo)) 
and c.filcode = tr.filcode
and  to_char(tr.annee) =:P_PARAM7;

select tr3.TAUX into t_recup_max
from taux_recup tr3
where tr3.ANNEE IN (SELECT MAX(tr2.ANNEE) 
from taux_recup tr2);

if (t_recup!=0 or t_recup!=null or t_recup!='0') then
		return t_recup;
else
		return t_recup_max;
end if;

-- Exception
exception
when no_data_found then 
return t_recup_max;

end;

      function CF_disponibleFormula return Number is
dispo_htr number(8,2);
begin
dispo_htr := :CS_budget - :CS_realise;
return dispo_htr; 
end;

